INSERT INTO `tb_product` (`id`, `name`, `desc`, `img`, `price`, `location`, `star`, `review`, `sold`, `unit`, `old_price`, `id_category`) VALUES
(1, 'Xoài cát Hoà Lộc', 'Xoài cát Hòa Lộc là loại xoài đặc sản nổi tiếng của vùng đồng bằng sông Cửu Long, một trong những loại trái cây chủ lực của Tiền Giang. Xoài cát Hòa Lộc là loại trái cây đặc sản của tỉnh Tiền Giang, và là sản phẩm trái cây đầu tiên của vùng đồng bằng sông Cửu Long được cấp bảo hộ chỉ dẫn địa lý.', 'https://amthuc.weba.vn/shop/images/amthuc/product/xoaikeo.png', 18000, 'Việt Nam', 42, 100, 615, '500g~1kg', 22000, 1),
(2, 'Thanh long ruột đỏ', 'Thanh long ruột đỏ được lòng rất nhiều người bởi màu sắc bắt mắt, vị ngọt đậm đà. Hàm lượng dinh dưỡng của thanh long loại ruột đỏ nhiều gấp đôi thanh long loại ruột trắng. Thanh long ruột đỏ có vị ngọt, mọng nước, lớp thịt trắng gần vỏ chua nhẹ không đáng kể.', 'https://amthuc.weba.vn/shop/images/amthuc/product/thanhlong.png', 25000, 'Việt Nam', 41, 87, 983, '500g~1kg', 30000, 1),
(3, 'Nho xanh Phan Rang', 'Không chỉ có hương vị thơm ngon mà Nho xanh Phan Rang còn là một loại trái cây bổ dưỡng. Thường xuyên ăn nho xanh có tác dụng tăng cường sức đề kháng, chống lão hóa, hỗ trợ tim mạch, có tác dụng đào thải độc tố... hiệu quả nên rất thích hợp bồi bổ người ốm yếu hay những ai muốn giữ vóc dáng và làn da.', 'https://product.hstatic.net/1000211685/product/upload_09a775b9dc654c65afeaa3fcf20a5779.jpg', 130000, 'Việt Nam', 45, 99, 341, '500g~1kg', 150000, 1),
(4, 'Dưa gang', 'Dưa gang hay còn có tên gọi khác là dưa bở do Organic phân phối, được lựa chọn từ những trái dưa chất lượng tại các trang trại sạch, đảm bảo vệ sinh an toàn thực phẩm. Loại dưa này cho thịt quả mềm xốp, dễ gọt vỏ, thái miếng hay xay sinh tố. Để giải nhiệt mùa hè, bạn có thể dùng loại quả này để dầm đường, làm kem hay xay sinh tố đều có tác dụng rất hữu hiệu', 'https://product.hstatic.net/1000211685/product/upload_77ef96b45848432caca7f6c68a8110c9.jpg', 90000, 'Việt Nam', 46, 78, 561, '500g~1kg', 130000, 1),
(5, 'Măng cụt', 'Măng cụt với hương vị chua chua ngọt ngọt, thanh mát đầy lôi cuốn và kích thích là loại trái cây không thể bỏ qua trong mùa hè này. Không chỉ mang đến nhiều công dụng cho sức khoẻ mà còn là \"thần dược\" cho nhan sắc của phụ nữ. Măng cụt thích hợp để ăn trực tiếp, giải khát cho ngày nóng nực', 'https://cdn.tgdd.vn/Products/Images/8788/239401/bhx/mang-cut-tui-1kg-8-10-trai-202207042041380519.jpg     ', 100000, 'Việt Nam', 28, 109, 321, '500g~1kg', 150000, 1),
(6, 'Chuối', 'Loại chuối được nhiều khách hàng ưa chuộng. Chuối chứa nhiều chất dinh dưỡng như kali, chất xơ, vitamin, ... nên chuối có tác dụng tốt cho sức khoẻ như hỗ trợ giảm cân, hỗ trợ sức khoẻ tim mạch, cải thiện sức khoẻ của thận. Cam kết đúng khối lượng, bao bì kín đáo, an toàn và bảo đảm hợp vệ sinh.', 'https://cdn.tgdd.vn/Products/Images/8788/272140/bhx/chuoi-gia-giong-nam-my-hop-1kg-6-7-trai-202209121642568468.jpg       ', 25000, 'Việt Nam', 46, 56, 456, '500g~1kg', 40000, 1),
(7, 'Trứng gà ', 'Hộp 6 trứng gà tươi, chất lượng, trứng to. Trứng được đóng trong hộp nhựa trong giúp bảo quản và vận chuyển trứng không bị móp, bể, hư.', 'https://cdn.tgdd.vn/Products/Images/8783/226998/bhx/hop-6-trung-ga-ta-tfood-6-trung-202112271033524364.jpg       ', 18000, 'Việt Nam', 45, 98, 761, 'Lốc 6 quả', 25000, 3),
(8, 'Trứng cút', 'Trứng cút tròn, đều. Trứng cút thì bạn có thể luộc chín chế biến thành một số món ăn như: trứng cút hun khói, xíu mại trứng cút,..', 'https://cdn.tgdd.vn/Products/Images/8783/219097/bhx/hop-30-trung-cut-tuoi-tfood-30-trung-202112271006004018.jpg', 27000, 'Việt Nam', 47, 168, 951, 'Lốc 30 quả', 35000, 3),
(9, 'Trứng vịt', 'Trứng vịt 4K Farm được sản xuất ở trang trại sạch, chất lượng, nên khách hàng có thể yên tâm về sản phẩm. Hộp 10 trứng vịt 4K Farm hộp trứng to, sạch, đảm bảo tốt cho sức khoẻ cả nhà. Trứng có kích thước khoảng 60-70g/trái.', 'https://cdn.tgdd.vn/Products/Images/8783/234812/bhx/hop-10-trung-vit-xanh-4kfarm-202208201622074650.jpg', 30000, 'Việt Nam', 49, 50, 645, 'Lốc 10 quả', 45000, 3),
(10, 'Trứng ngỗng', 'Trứng ngỗng lành tính. Do đó, trứng ngỗng rất tốt đối với sức khỏe con người đặc biệt với các bà bầu, mẹ bầu có thể ăn vào bất kỳ thời gian nào trong thai kỳ. Không cần phải băn khoăn về tác động của chúng đối với sức khỏe của mẹ hay sự phát triển của bé.', 'https://64.media.tumblr.com/tumblr_m16akyWIXI1rnsyxno1_1280.png', 30000, 'Việt Nam', 49, 23, 95, '1 quả', 38000, 3),
(11, 'Trứng muối', 'Hộp 4 trứng vịt muối được đóng gói và bảo quản theo những tiêu chuẩn nghiêm ngặt về vệ sinh và an toàn thực phẩm, đảm bảo về chất lượng của thực phẩm, xuất xứ rõ ràng. Trứng vịt to tròn, đều. Đây là sản phẩm cỏ thể ăn liền, có thể ăn với cơm hoặc ăn không, ...', 'https://cdn.tgdd.vn/Products/Images/8783/222975/bhx/hop-4-trung-vit-muoi-tfood-4-trung-202112271027398426.jpg', 35000, 'Việt Nam', 48, 44, 165, 'Lốc 4 quả', 50000, 3),
(12, 'Xà lách xoong Đà Lạt', 'Rau xà lách xoong Đà Lạt có hương vị tươi mát, ngon ngọt được nhiều người tiêu dùng ưa chuộng. Trong xà lách xoong có chứa hàm lượng chất xơ cao, dinh dưỡng giúp tăng cường sức khỏe, khả năng chống oxi hóa tốt cũng như hỗ trợ chị em trong chăm sóc sắc đẹp', 'https://cdn.tgdd.vn/Products/Images/8820/271509/bhx/xa-lach-xoong-da-lat-bo-500g-202205181608274117.jpg        ', 20000, 'Việt Nam', 31, 102, 156, '300g~500g', 32000, 2),
(13, 'Rau muống Organic', 'Giá trị dinh dưỡng của Rau muống đối với sức khoẻ: Trong nhiều bữa cơm gia đình của người Việt, thường xuất hiện các món chế biến từ rau muống như: rau muống luộc, rau muống xào, rau muống làm gỏi, nấu cháo thịt rau muống. Rau muống chứa vitamin C, E, chất béo, khoáng chất như sắt, kẽm, magie.', 'https://product.hstatic.net/1000126467/product/05479276_93c5d547e9244acaa8eef1a38b426014_a4905b67a33b431a8928b58ce2b75325_master.jpg', 20000, 'Việt Nam', 46, 47, 612, '300g~500g', 27000, 2),
(14, 'Cải xanh', 'Cải bẹ xanh baby được trồng bằng kỹ thuật canh tác chuyên biệt, có thời gian thu hoạch ngắn hơn 1 nửa so với thông thường, là rau non nên có độ giòn, ngọt hơn rất nhiều. Giữ được nguyên vẹn các giá trị dinh dưỡng.', 'https://cdn.tgdd.vn/Products/Images/8820/223329/bhx/cai-be-xanh-baby-goi-300g-202205181634539404.jpg', 12000, 'Việt Nam', 50, 89, 308, '300g~500g', 20000, 2),
(15, 'Cà rốt Đà Lạt', 'Cà rốt Đà Lạt là một loại củ rất quen thuộc trong các món ăn của người Việt. Cà rốt có hàm lượng chất dinh dưỡng và vitamin A cao, được xem là nguyên liệu cần thiết cho các món ăn dặm của trẻ nhỏ, giúp trẻ sáng mắt và cung cấp nguồn chất xơ dồi dào', 'https://cdn.tgdd.vn/Products/Images/8785/271572/bhx/ca-rot-da-lat-tui-500g-2-5-cu-202205201603295625.jpg', 20000, 'Việt Nam', 50, 354, 981, '500g~1kg', 34000, 2),
(16, 'Củ cải trắng', 'Củ cải trắng trồng ở Đà Lạt là một loại rau củ vừa có thể dùng để làm thực phẩm, vừa có thể sử dụng để làm dầu hạt cải. Củ cải trắng chứa rất nhiều vitamin và khoáng chất, tốt cho cơ thể.', 'https://cdn.tgdd.vn/Products/Images/8785/271469/bhx/cu-cai-trang-tui-500g-2-5-cu-202205201700108859.jpg', 15000, 'Việt Nam', 44, 99, 200, '500g~1kg', 30000, 2),
(17, 'Trà xanh ', 'Từ những cây trà có tuổi đời hàng trăm năm do nhiều thế hệ từ xa xưa để lại hoàn toàn tự nhiên hầu như không có sự can thiệp của con người. Trà xanh có màu nước xanh óng ánh ánh vàng, phảng phất hương thơm của núi rừng Tây Bắc, vị thanh, ngọt hậu giữ lâu trong cổ họng.', 'https://cdn.tgdd.vn/Products/Images/2385/224780/bhx/tra-xanh-huu-co-vherbs-shan-tuyet-co-thu-49g-202007230825591357.jpg', 56000, 'Nhật Bản', 43, 91, 206, '200g~500g', 65000, 6),
(18, 'Cà phê nguyên hạt rang mộc The JUN Passion', 'Từ những hạt cà phê ngon nhất tại vùng đất đỏ bazan kết hợp với công thức rang xay mộc độc quyền, có thể vừa pha phin, vừa pha máy. Ngụm đầu của ly cà phê The JUN Passion có thể đọng lại vị đắng đậm cùng một chút chua thanh đặc trưng của cà phê nguyên chất trên đầu lưỡi', 'https://cdn.tgdd.vn/Products/Images/2883/207792/bhx/ca-phe-rang-xay-moc-the-jun-passion-345g-202001080955013927.jpg', 180000, 'Brazil', 46, 56, 210, '500g~1kg', 240000, 7),
(19, 'Cà phê rang xay mộc The JUN Challenge', 'Sản phẩm là sự hòa quyện giữa vị của các hạt cà phê cùng với công thức ủ độc quyền từ các chuyên gia của The JUN chính là điểm đặc biệt và khác biệt của cà phê The JUN đối với các sản phẩm cà phê khác.', 'https://cdn.tgdd.vn/Products/Images/2883/207793/bhx/ca-phe-rang-xay-moc-the-jun-challenge-345g-202001080954054227.jpg', 180000, 'Brazil', 47, 81, 150, '500g~1kg', 250000, 7),
(20, 'Bột trà xanh Matcha Natsu', 'Bột trà xanh Matcha được nhập khẩu 100% từ Nhật Bản, sản phẩm là tự nhiên và nguyên chất, không chứa bất kì hóa chất hay hương liệu nào khác', 'https://matchashop.vn/upload/images/bot-tra-xanh-matcha-natsu-1kg.jpg', 305000, 'Nhật Bản', 43, 93, 189, '200g~500g', 335000, 6),
(21, 'Trái Cây Khô Hàn Quốc 200gr', 'Trái cây khô hỗn hợp là nguyên liệu làm bánh được sử dụng rộng rãi trong các món bánh, có mùi vị thơm ngon và giàu chất dinh dưỡng, đảm bảo vệ sinh an toàn thực phẩm.', 'https://product.hstatic.net/1000186075/product/8809319240341_9f5410dc36514bb180c1718e0a82b6bf_master.jpg', 65000, 'Hàn Quốc', 33, 41, 192, '200g', 80000, 10),
(22, 'Mít sấy', ' Mít rất giàu các vitamin nhóm B (B1, B2, B6), vitamin C rất tốt cho hệ tiêu hóa và phụ nữ mang thai. Mít chứa rất ít lượng chất béo bão hòa, cholesterol và natri giúp tăng cường hệ thống miễn dịch, tốt cho tim mạch. Đường thiên nhiên (fructose và sucrose) trong mít giúp bổ sung năng lượng cho cơ thể.', 'https://cf.shopee.vn/file/ead70e7800660bdaa19efe3455425dc7', 70000, 'Việt Nam', 41, 41, 100, '300g~500g', 88000, 10),
(23, 'Sầu Riêng Sấy Giòn', 'Sầu riêng sấy hương vị thơm ngon, bổ dưỡng, nguyên chất có vị ngọt nhẹ rất đặc trưng, béo vừa chứ không ngậy pha lẫn vào nhau tuyệt vời đến độ khó diễn tả khiến nhiều người thích đến phát nghiện, hoàn toàn không sử dụng hóa chất và phụ gia thực phẩm. Sầu riêng sấy được đóng gói tiện dụng, thuận lợi mang theo mỗi khi đi xa hay dành làm quà tặng vì nó rất lạ, ngon, mang hương vị thuần Việt', 'http://traicayhatsay.com/upload/images/sau-rieng-say-dat-biet-200g-cua-hang-trai-cay-hat-say.jpg', 220000, 'Việt Nam', 43, 22, 98, '300g~500g', 300000, 10),
(24, 'Mứt dâu', 'Loại trái cây này ích lợi cho sức khoẻ vì dồi dào chất chống ôxy hoá (giúp giữ ổn định hệ thống tim mạch), chất chống viêm (ngừa ung thư hữu hiệu), giàu vitamin C (giúp tăng sức đề kháng), magiê (giúp giữ gìn sức khoẻ xương, răng), kali (bảo vệ cơ bắp, hệ thần kinh, giảm nguy cơ huyết áp cao)', 'https://cf.shopee.vn/file/d654e572fd60f05f429dce78175d067e', 80000, 'Việt Nam', 39, 45, 89, '300g~500g', 110000, 10),
(25, 'Khoai lang vàng sấy dẻo', 'Có rất nhiều loại rau củ có thể được chế biến thành dạng chip và ăn cũng giòn như bí đỏ, cà rốt, củ cải đường, củ cải đỏ…nhưng thậm chí còn thơm ngon và giàu dinh dưỡng hơn. Một trong số đó không thể không nhắc tới khoai lang sấy giòn rất thơm ngon và hấp dẫn lắm đấy nhé, đảm bảo cả gia đình bạn sẽ thích thú cho mà xem.', 'https://vn-live-05.slatic.net/p/37a362afe413b099d7871d41eb924eca.png_525x525q80.jpg', 70000, 'Hàn Quốc', 42, 48, 102, '300g~500g', 98000, 10),
(26, 'Sữa thanh trùng', 'Được chế biến từ sữa bò tươi nguyên chất từ cao nguyên Lâm Đồng - nơi có nguồn nguyên liệu sữa tươi chất lượng cao được vắt từ những giống bò sữa tốt nhất trong điều kiện khí hậu trung bình từ 15 - 25oC và độ cao trên 1000 m.', 'https://suadalat.com/wp-content/uploads/2021/09/STTT-KD-950ml-1.jpg', 12000, 'Việt Nam', 49, 99, 249, '950ml', 18000, 9),
(27, 'Sữa chua lên men đào', 'Với thành phần chủ yếu từ sữa bò tươi nguyên chất Dalatmilk và nước cốt đào. Bổ sung vitamin C cho cơ thể, mang lại sự sảng khoái mà còn tăng cường sức đề kháng cho cơ thể của bạn với lợi khuẩn LH - BO2 (lợi khuẩn kích thích quá trình hấp thu chất dinh dưỡng cho cơ thể).', 'https://product.hstatic.net/1000282430/product/sua-chua-an-mut-dao-hu-100g_87d548dcb6884842bc2758eecd3b4f1c.jpg', 10000, 'Việt Nam', 43, 88, 329, '100g', 17000, 9),
(28, 'Phô mai Bottega Zelachi Gouda', 'Là sản phẩm phô mai có xuất xứ từ Hà Lan, được làm từ thành phần tự nhiên đến từ thương hiệu phô mai Bottega Zelachi. Phô mai Bottega Zelachi Gouda gói 100g có vị ngọt mặn, có thể dùng trực tiếp với trái cây, bánh mì, bánh crackers, kẹp với sandwich, có thể dùng để làm sốt hoặc súp.', 'http://product.hstatic.net/1000282430/product/upload_7d45246b8280481ba411fda90c468904_grande.jpg', 50000, 'Pháp', 44, 79, 341, '100g~500g', 63000, 9),
(29, 'Phô mai lát Anchor Cheddar', 'Sản phẩm phô mai của thương hiệu Anchor có hương vị thơm ngon, lưu giữ được hương vị tinh túy từ sữa, an toàn cho sức khoẻ của người dùng. Sản phẩm Phô mai lát Anchor Cheddar gói 200g có vị béo, mặn được làm từ 100% nguyên liệu sạch từ Ba Lan, có thể dùng để chế biến nhiều món ăn.', 'https://exclusivelyfood.co.za/wp-content/uploads/2020/03/ALFALFA-CHEDDAR-CHEESE-PKg.jpg', 75000, 'Pháp', 40, 55, 298, '100g~500g', 80000, 9),
(30, 'Gạo thơm A An ST21 ', 'Gạo thơm A An ST21 túi 5kg được thu hoạch từ giống lúa ST21 tự nhiên. Gạo A An được sản xuất trên dây chuyền hiện đại, cam kết không đấu trộn, không chất tạo mùi, mang lại sản phẩm gạo chất lượng, an toàn cho sức khoẻ người dùng', 'https://cdn.tgdd.vn/Products/Images/2513/223652/bhx/gao-thom-a-an-st21-tui-5kg-202006061602569575.jpg', 160000, 'Việt Nam', 37, 78, 478, '5kg', 178000, 4),
(31, 'Gạo lứt đỏ Vinh Hiển', 'Giống gạo được tuyển chọn từ vùng trồng gạo nổi tiếng kết hợp với công nghệ sản xuất đạt chuẩn HACCP. Gạo lứt đỏ Vinh Hiển túi 1kg thích hợp cho những người muốn ăn kiêng, giảm cân. Gạo Vinh Hiển chất lượng, an toàn, sạch và không chứa chất kích thích tăng trưởng.', 'https://cdn.tgdd.vn/Products/Images/2513/229221/bhx/gao-lut-do-vinh-hien-tui-1kg-202010171224597621.jpg', 35000, 'Việt Nam', 42, 102, 601, '1kg', 44000, 4),
(32, 'Nếp cái hoa vàng Vinh Hiển', 'Gạo có đặc tính cơm mềm dẻo, thơm ngon. Được canh tác từ nếp cái hoa vàng Bắc, mang đến chất lượng cao. Nếp cái hoa vàng Vinh Hiển túi 1kg dinh dưỡng, an toàn và dẻo. Gạo Vinh Hiển chất lượng, an toàn, sạch và không chứa chất kích thích tăng trưởng.', 'https://cdn.tgdd.vn/Products/Images/2513/227004/bhx/nep-cai-hoa-vang-vinh-hien-tui-1kg-202008150913276084.jpg', 36000, 'Việt Nam', 48, 200, 439, '1kg', 41000, 4),
(33, 'Gạo thơm Neptune ST24', 'Gạo khi nấu cho cơm mềm, dẻo nhiều, ngon miệng tạo cảm giác dễ chịu cho người ăn. Gạo thơm Neptune ST24 túi 5kg là giống lúa ST24 cao cấp của hãng Neptune, được nuôi trồng canh tác theo các quy trình nghiêm ngặt, đảm bảo chất lượng và an toàn đến tay người dùng.', 'https://cdn.tgdd.vn/Products/Images/2513/224495/bhx/gao-thom-neptune-st24-tui-5kg-202006250849282085.jpg', 159000, 'Việt Nam', 50, 189, 349, '5kg', 165000, 4),
(34, 'Gạo Lạc Việt XK51', 'Gạo Lạc Việt là hãng gạo uy tính, nổi tiếng tại thị trường gạo của Việt Nam, được rất nhiều khách hàng tin dùng chọn lựa bởi hương vị thơm ngon, dẻo mềm khó cưỡng. Gạo Lạc Việt XK51 túi 5kg với túi xách tiện dụng, hạt gạo trắng đều cho cơm dẻo, mềm thơm lừng bắt vị cho bữa ăn ngon.', 'https://cdn.tgdd.vn/Products/Images/2513/279509/bhx/gao-lac-viet-xk51-tui-5kg-202205211712336834.jpg', 120000, 'Việt Nam', 41, 99, 210, '5kg', 143000, 4),
(35, 'Rượu Soju 16.9% 360ml', 'Rượu chamisul fresh soju được biết đến là loại rượu tinh khiết nguyên liệu sạch và được lọc sạch qua than tre thông qua các phương pháp tự nhiên. Hương vị tươi mát nhưng những giọt sương được tinh chế qua than tre. Cùng với việc sử dụng than tre hoạt tính tối ưu đã được xử lý vật thể tồn lưu gây say, rượu jinro hàn quốc Chamisul Fresh làm gia tăng hương vị tinh khiết tự nhiên và mang tới một buổi sáng ngày hôm sau vẫn hoàn toàn sảng khoái. ', 'https://salt.tikicdn.com/cache/w1200/ts/product/ee/fc/96/db25bd49043d277ee8d4d731a637bbfa.jpg', 59000, 'Hàn Quốc', 47, 83, 298, '360ml', 65000, 12),
(36, 'Hạt điều vỡ rang muối', 'Hạt điều Bình Phước nổi tiếng, đặc biệt sản phẩm này là loại điều hạt to, thơm ngon. Hạt điều vỡ nhưng vẫn giữ được độ béo ngậy, thơm ngon.', 'https://salt.tikicdn.com/cache/750x750/ts/product/d2/bf/15/d265c517d28344c5adc0c0059197acbb.jpg.webp', 55000, 'Việt Nam', 46, 78, 178, '500g', 60000, 8),
(37, 'Bún khô Long Quan 500g', 'Bún khô từ lâu đã là đặc sản và là niềm tự hào của thủ đô Hà Nội và là một trong những món ăn trứ danh trên bản đồ ẩm thực thế giới', 'https://salt.tikicdn.com/cache/750x750/ts/product/99/5e/f2/0b3b92054478ffe1b5b7246a20c2e7be.png.webp', 37000, 'Việt Nam', 43, 132, 231, '250g', 40000, 5),
(38, 'Gạo trắng Thiên Nhật', 'Gạo Thiên Nhật chất lượng, không chứa chất bảo quản, tạo mùi hay tẩy trắng. Gạo trắng Thiên Nhật túi 5kg cho ra cơm mềm, nở cơm, giúp ăm ngon miệng hơn. ', 'https://cdn.tgdd.vn/Products/Images/2513/282955/bhx/gao-trang-thien-nhat-tui-5kg-202206220935467038.jpg', 65000, 'Việt Nam', 40, 88, 128, '2kg~5kg', 77000, 4),
(39, 'Gạo Hoa Lúa xanh', 'Gạo khi nấu có vị dẻo, vị thơm đặc trưng sẽ kích thích vị giác giúp thưởng thức các món ăn khác tuyệt vời hơn. Gạo Hoa Lúa xanh với hạt nhỏ, thơm nhẹ, nở vừa và ít dẻo, đảm bảo an toàn, không tẩy trắng, không chứa chất bảo quản.', 'https://cdn.tgdd.vn/Products/Images/2513/230179/bhx/gao-hoa-lua-xanh-tui-5kg-202011041002023220.jpg', 125000, 'Việt Nam', 43, 102, 178, '2kg~5kg', 137000, 4),
(40, 'Gạo Home Rice Bảo Bình', 'Hạt gạo thon dài, trắng đẹp giúp cho từng hạt cơm trở nên đẹp mắt và lôi cuốn người dùng. Khi nấu cho cơm dẻo nhiều, dai cơm, ít nở cùng với hương thơm nhẹ mùi lá dứa đặc trưng của giống gạo, lôi cuốn người ăn trong từng món ăn, để nguội, hạt cơm lại càng mềm dẻo thơm ngon, không bị cứng.', 'https://cdn.tgdd.vn/Products/Images/2513/232821/bhx/gao-home-rice-bao-binh-tui-5kg-202101052250086109.jpg', 142000, 'Việt Nam', 40, 208, 312, '2kg~5kg', 160000, 4),
(41, 'Gạo Nhật Japonica A An', 'Gạo Nhật Japonica A An có đặc tính của dòng gạo này là dẻo nhiều, cơm khá mềm nhưng lại nở ít. Khi ăn cực kì thơm và ngon miệng kèm theo độ dẻo của gạo thì không có gì sánh bằng. Gạo này cũng là một giống Nhật Japonica. được chăm sóc theo quy trình khoa học, không chất kích thích tăng trưởng.', 'https://cdn.tgdd.vn/Products/Images/2513/223660/bhx/gao-nhat-japonica-a-an-tui-5kg-202111051553389653.jpg', 110000, 'Nhật Bản', 42, 134, 187, '2kg~5kg', 125000, 4),
(42, 'Gạo Nhật hữu cơ Ecoba Anh Đào', 'Gạo Nhật hữu cơ Ecoba Anh Đào giàu khoáng chất, chất dinh dưỡng, bổ sung sức đề kháng rất tốt cho trẻ. Tính đến nay thì gạo này được chứng nhận hữu cơ, tiêu chuẩn an toàn thực phẩm nên các bà mẹ đừng lo về chất lượng sản phẩm nhé. Khi nấu cơm theo tỉ lệ 1 chén gạo thì cho 2.5 chén nước và nấu trong 15 - 20 phút thì hạt gạo sẽ rất ngon.', 'https://cdn.tgdd.vn/Products/Images/2513/203893/bhx/gao-nhat-huu-co-ecoba-anh-dao-hop-1kg-201906210956166088.jpg', 135000, 'Nhật Bản', 40, 167, 209, '2kg~5kg', 150000, 4),
(43, 'Gạo Nhật Bản Vinh Hiển Taiyo', 'Loại gạo giống Nhật Bản Vinh Hiển Taiyo có đặc điểm hạt gạo tròn, đều đặn. Khi nấu hạt nở đều, cơm dẻo ngọt và thơm nhẹ. Gạo này là một giống gạo Nhật Japonica', 'https://cdn.tgdd.vn/Products/Images/2513/197280/bhx/gao-giong-nhat-ban-vinh-hien-taiyo-tui-5kg-202007211001455095.jpg', 156000, 'Nhật Bản', 39, 200, 230, '2kg~5kg', 167000, 4),
(44, 'Gạo Trạng Nguyên Vinh Hiển ST25', 'Gạo Vinh Hiển là thương hiệu lớn với sản phẩm Gạo đặc sản trạng nguyên Vinh Hiển ST25, mềm và thơm giúp ăn ngon miệng, rất hợp khẩu vị nhiều người. Túi nhỏ thích hợp cho sử dụng cá nhân hoặc dùng thử.', 'https://cdn.tgdd.vn/Products/Images/2513/286570/bhx/gao-dac-san-trang-nguyen-vinh-hien-st25-tui-2kg-202209131401151078.jpg', 95000, 'Việt Nam', 46, 300, 307, '2kg~5kg', 110000, 4),
(45, 'Gạo Vinh Hiển Đỗ Quyên', 'Gạo Đỗ Quyên là một trong những dòng sản phẩm gạo đóng túi an toàn thương hiệu Gạo Vinh Hiển. Gạo Vinh Hiển Đỗ Quyên được trồng tại các cánh đồng tập trung và chăm bón cẩn thận, cho hạt dài, màu trắng trong. Gạo cho cơm dẻo vừa, mềm ngon và hương thơm nhẹ tự nhiên.', 'https://cdn.tgdd.vn/Products/Images/2513/193609/bhx/gao-vinh-hien-do-quyen-tui-5kg-201906210945327894.jpg', 84000, 'Việt Nam', 47, 209, 230, '2kg~5kg', 95000, 4),
(46, 'Gạo Vinh Hiển Thông Dụng', 'Nở xốp, cơm mềm. Gạo Vinh Hiển Thông Dụng sạch, an toàn, cơm ngon. Cơm nấu ra giúp ăn ngon miệng, phù hợp cho cả gia đình. Gạo Vinh Hiển được nhiều người tin dùng vì chất lượng, quy trình canh tác nghiêm ngặt.', 'https://cdn.tgdd.vn/Products/Images/2513/207897/bhx/gao-vinh-hien-tui-5kg-201908071655014644.JPG', 81000, 'Việt Nam', 49, 350, 400, '2kg~5kg', 89000, 4),
(47, 'Gạo yến mạch nguyên hạt WellBeing', 'Đây là loại yến mạch nguyên hạt đã được loại bỏ lớp vỏ cứng bên ngoài, có hạt dài màu nâu. Có thể dùng để thay thế cơm cho bữa ăn của gia đình hoặc xay làm bột rồi chế biến các món ăn theo ý thích.', 'https://cdn.shopify.com/s/files/1/0617/2497/products/gao-lua-mach-wellbeing_720x@2x.png?v=1596786522', 150000, 'Hàn Quốc', 39, 200, 209, '800g~2kg', 170000, 4),
(48, 'Gạo ngũ cốc 15 loại hạt Dasaeng', 'Với nhiều loại ngũ cốc khác nhau được pha trộn cùng gạo trắng, món cơm này không chỉ cung cấp nhiều dưỡng chất thiết yếu cho cơ thể mà còn có thể dùng để ăn kiêng nhằm hạn chế tinh bột.', 'https://cdn.shopify.com/s/files/1/0617/2497/products/z1661970586076_2dce4a47e7c572c557f08d07b336a605-removebg-preview_720x@2x.png?v=1576558944', 194000, 'Hàn Quốc', 30, 187, 234, '800g~2kg', 210000, 4),
(49, 'Gạo nếp cẩm WellBeing', 'Chứa hàm lượng protein và chất béo cao hơn các loại gạo khác cùng vị ngọt đặc trưng, Gạo nếp cẩm ngày càng được yêu thích và sử dụng nhiều hơn trong chế biến các món ăn.', 'https://cdn.shopify.com/s/files/1/0617/2497/products/gao-nep-cam-wellbeing_720x@2x.png?v=1596786338', 120000, 'Hàn Quốc', 32, 145, 179, '800g~2kg', 135000, 4),
(50, 'Gạo lứt WellBeing', 'Là một trong những loại gạo có chứa nhiều thành phần dinh dưỡng tốt cho cơ thể con người như tinh bột, chất đạm, chất xơ, vitamin B1, B2, canxi, sắt, magie,... gạo lứt ngày càng được ưa chuộng và sử dụng nhiều hơn ở các hộ gia đình.', 'https://cdn.shopify.com/s/files/1/0617/2497/products/gao-lut-wellbeing_720x@2x.png?v=1596783358', 124000, 'Hàn Quốc', 33, 167, 200, '800g~2kg', 130000, 4),
(51, 'Gạo nếp WellBeing', 'Gạo có hạt ngắn khá tròn, màu trắng đục. Khi nấu chín sẽ kết dính và có độ dẻo thơm thích hợp để làm các món bánh hoặc nấu xôi.', 'https://cdn.shopify.com/s/files/1/0617/2497/products/gao-nep-wellbeing_720x@2x.png?v=1596786426', 94000, 'Hàn Quốc', 35, 109, 120, '800g~2kg', 105000, 4),
(52, 'Gạo tẻ Nhật Niigata Koshihikari', 'Đây là loại gạo thơm ngon và được người Nhật Bản sử dụng hằng ngày trong mỗi bữa ăn, Những bữa tiệc liện hoan ở Nhật thì thứ gạo này không thể thiếu trên bàn ăn. Với hương thơm, vị ngọt đậm đà hạt gạo tròn, khi nấu chín ta thấy hạt cơm trắng sáng, dẻo thơm rất đẹp mắt và giàu chất dinh dưỡng.', 'https://thucduong.org/wp-content/uploads/2019/11/gao-te-nhat-2kg.jpg', 210000, 'Nhật Bản', 38, 208, 230, '2kg~5kg', 222000, 4),
(53, 'Bơ thực vật Tường An', 'Sản phẩm bơ thực vật Tường An được làm chủ yếu từ dầu thực vật tinh luyện, nước, muối, hương bơ và các chất phụ gia khác. Đặc biệt, sản phẩm có chứa Vitamin A tốt cho sức khỏe. Sản phẩm không chứa chất béo chuyển hóa và cholesterol nên bạn hoàn toàn có thể yên tâm sử dụng.', 'https://bizweb.dktcdn.net/100/383/111/products/bmargarine200g-f.jpg?v=1587309024163', 45000, 'Việt Nam', 42, 134, 200, '100g~200g', 50000, 9),
(54, 'Bơ mặn Even', 'Sản phẩm được nhập khẩu trực tiếp về Việt Nam với nguồn nguyên liên hảo hạng đạt tiêu chuẩn châu Âu. Vì vậy khách hàng có thể hoàn toàn yên tâm về chất lượng của sản phẩm. Sản phẩm được làm từ thành phần có nguồn gốc tự nhiên với 98% bơ và 2% muối.', 'https://cf.shopee.vn/file/5a928ac396d97de51a31c00b7cdae3d8', 98000, 'Pháp', 45, 203, 240, '100g~200g', 110000, 9),
(55, 'Bơ lạt marquis', 'Bơ lạt marquis chứa thành phần chính gồm chất béo từ thực vật, nước, bơ, và một số chất phụ gia khác. Đặc biệt, sản phẩm chứa nhiều đạm, Vitamin A và vitamin D và các chất dinh dưỡng thiết yếu cho cơ thể. Bơ lạt marquis Elle & Vire có hương vị thơm ngon đặc trưng. Khi sử dụng bạn sẽ cảm nhận được vị bơ đậm đà thơm hơn những loại bơ thông thường.', 'https://cdn.tgdd.vn/Products/Images/3465/222474/bhx/bo-lac-marquis-80-beo-elle-vire-goi-200g-202111272256302477.jpg', 45000, 'Pháp', 40, 210, 250, '100g~200g', 55000, 9),
(56, 'Bơ đậu phộng mịn Golden Farm', 'Bơ đậu phộng mịn Golden Farm là loại bơ thực vật được làm từ đậu phộng hảo hạng cùng các loại gia vị tự nhiên. Điểm cộng của sản phẩm là không chứa chất phụ gia thực phẩm, hoàn toàn bổ dưỡng và tốt cho sức khỏe người tiêu dùng. Sản phẩm có hương vị thơm béo tự nhiên, chất bơ sánh mịn hòa quyện mang đến cho gia đình bạn những bữa ăn ngon, tiện lợi và đảm bảo dinh dưỡng.', 'https://cdn.tgdd.vn/Products/Images/3465/174516/bhx/bo-dau-phong-min-golden-farm-hu-170g-201911201533371675.jpg', 40000, 'Việt Nam', 40, 300, 310, '170g~500g', 56000, 9),
(57, 'Sữa tươi tiệt trùng không đường Nutimilk', 'Là dòng sản phẩm sữa tươi 100 điểm của Nutimilk với hương vị thơm ngon, dinh dưỡng chất lượng đạt chuẩn. Sữa tươi tự nhiên, hàm lượng dinh dưỡng tương đương sữa nhập nên được các bà mẹ ưa chuộng.', 'https://cdn.tgdd.vn/Products/Images/2386/253584/bhx/sua-tuoi-tiet-trung-khong-duong-nutimilk-100-diem-hop-1-lit-202111031558127559.jpg', 35000, 'Việt Nam', 39, 198, 209, '500ml~1 lít', 44000, 9),
(58, 'Sữa Gạo Organic Australias Own', 'Sữa gạo Australias Own Organic được làm từ các nguyên liệu chọn lọc an toàn, đạt chứng nhận Organic. Sản phẩm không chất bảo quản, không chứa chất béo và gluten, bổ sung nhiều dưỡng chất có lợi cho sức khỏe, giúp tăng cường sức đề kháng. Với sữa gạo Organic Australias Own, bạn có thể dùng uống trong bữa sáng, bữa trưa hay tối đều thích hợp cũng như có thể kết hợp với đa dạng các loại thực phẩm khác nhau.', 'https://media.bibomart.com.vn/resize.1170x-/media/catalog/product/s/u/sua-gao-organic-uc-1l-804898-1.jpg', 125000, 'Úc', 44, 349, 450, '500ml~1 lít', 12000, 9),
(59, 'Sữa Devondale Nguyên Kem', 'Sữa được biết đến là một trong những thức uống bổ dưỡng nhất thế giới, giá trị sinh học của sữa đã được khoa học chứng minh tác động trực tiếp lên sự tăng trưởng của cơ thể đặc biệt là chiều cao. ', 'https://suachobeyeu.vn/application/upload/products/sua-tuoi-nguyen-kem-devondale-uc-hop-1-lit.jpg', 365000, 'Úc', 42, 192, 201, '1 lít~2 lít', 380000, 9),
(60, 'Sữa Hạt Óc Chó Nguyên Chất 137 Degrees', 'Đây là sản phẩm sữa của Thái Lan với thành phần hữu cơ bổ dưỡng, mùi vị thơm, béo của hạt tươi kết hợp với vị ngọt dịu nhẹ của mật hoa dừa, đảm bảo giữ nguyên hương vị và hàm lượng dinh dưỡng. Đặc biệt sữa được đóng gói dạng hộp 1 lít tiện dùng.', 'https://salt.tikicdn.com/cache/750x750/ts/product/9d/a6/70/3c0d0fe6ed284a99ee575a66fdbef8d3.png.webp', 150000, 'Úc', 47, 304, 382, '1 lít', 220000, 9),
(61, 'Phô Mai Mozzarella dạng khối', 'Được chế biến bằng cách kết đông và lên men từ sữa bò nên có hàm lượng đạm, chất béo, protein, vitamin, các khoáng chất và canxi cao. Có thể kết đông sữa tạo thành phô mai. Được sản xuất theo quy trình khép kín, tiêu chuẩn Châu Âu, mỗi miếng phô mai Mozzarella là sản phẩm của sự tỉ mỉ và chọn lọc kĩ lưỡng từ khâu nguyên liệu đầu vào chất lượng tốt nhất.', 'https://s3-eu-west-1.amazonaws.com/webshop/data/thumbs/55/55dc36424175f1e57ceb1786ba8a0e54da377fdc.jpg', 360000, 'Pháp', 39, 120, 298, '1 kg', 570000, 9),
(62, 'Phô mai Con Bò Cười ', 'Phô mai Con Bò Cười là thương hiệu phô mai quá quen thuộc đối với người tiêu dùng Việt Nam trong nhiều năm qua. Phô mai Con Bò Cười hộp 120g (8 miếng) vị thơm ngon mềm mịn, là nguồn cung cấp dưỡng chất canxi + vitamin D bổ sung kẽm + Vitamin B 12 hỗ trợ sự phát triển thể chất.', 'https://cdn.tgdd.vn/Products/Images/7599/201194/bhx/pho-mai-con-bo-cuoi-hop-120g-8-mieng-202209091323350769.jpg', 34000, 'Việt Nam', 42, 100, 108, '6 miếng~8 miếng', 40000, 9),
(63, 'Phô mai Gouda Bottega Zelachi', 'Là sản phẩm phô mai có xuất xứ từ Hà Lan, được làm từ thành phần tự nhiên đến từ thương hiệu phô mai Bottega Zelachi. Phô mai Bottega Zelachi Gouda gói 100g có vị ngọt mặn, có thể dùng trực tiếp với trái cây, bánh mì, bánh crackers, kẹp với sandwich, có thể dùng để làm sốt hoặc súp.', 'https://cdn.tgdd.vn/Products/Images/7599/248425/bhx/pho-mai-bottega-zelachi-gouda-goi-100g-202110141155160208.jpg', 50000, 'Việt Nam', 50, 400, 480, '100g~500g', 55000, 9),
(64, 'Phô mai Ile De France Petit Camembert', 'Thương hiệu Ile de France được mệnh danh là vua phô mai của Pháp, đã có từ thế kỷ thứ 9. Sản phẩm Phô mai Ile De France Petit Camembert hộp 125g được làm bằng các phương pháp truyền thống và nguồn sữa tươi có sẵn rất mịn, ngon và đảm bảo độ tươi tuyệt đối.', 'https://cdn.tgdd.vn/Products/Images/7599/256851/bhx/pho-mai-ile-de-france-petit-camembert-hop-125g-202111061652274529.jpg', 100000, 'Pháp', 41, 230, 240, '125g~250g', 120000, 9),
(65, 'Phô Mai Camembert Paysan Breton', 'Những sản phẩm từ sữa nói chung và phô mai nói riêng cung cấp vitamin A và vitamin D phong phú, có lợi cho đường tiêu hóa, giúp hấp thu thức ăn tốt hơn và cải thiện sức khỏe. Sản phẩm Phô mai Camembert Paysan Breton được sản xuất theo công thức đặc biệt, mang lại hương vị đặc trưng thơm ngon của sữa', 'https://product.hstatic.net/1000126467/product/01209907_43b1743bec5c4c28b82faa8911e28bb4_master.jpg', 97000, 'Pháp', 32, 120, 198, '125g~250g', 120000, 9),
(66, 'Phomai Trái Cây Nhật Bản QBB', 'Phomai cung cấp dinh dưỡng và năng lượng cho bé như 1 thực phẩm thiết yếu ở các nước phát triển. Phomai chiết xuất từ sữa bò nên có nhiều canxi, phomai được lên men nên có nhiều vi khuẩn có lợi cho đường tiêu hoá.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRargktm-33IiW-SGXTjjDHsfqoxofqnmok_Q&usqp=CAU', 110000, 'Nhật Bản', 32, 76, 88, '125g~250g', 143000, 9),
(67, 'Phô mai Nhật Emina', 'EMINA là tên được ghép lại từ từ Emi có nghĩa là \"Nụ cười\" và từ Mina có nghĩa là \"Mọi người\". EMINA nghĩa là mọi người cùng vui vẻ. Đây cũng chính là giá trị cốt lõi mà hãng luôn tâm niệm từ khi thành lập. Nhờ những viên phomai thơm ngon, QBB hy vọng có thể đem lại niềm vui, niềm hạnh phúc tới toàn thế giới.', 'https://image.rakuten.co.jp/misonoya/cabinet/202205_07/r2_a416-3-1.jpg', 135000, 'Nhật Bản', 41, 64, 102, '125g~250g', 165000, 9),
(68, 'Tần Ô VietGAP', 'Với những đặc tính không độc, vị ngọt, tính mát, thơm, hơi đắng và the, tần ô có rất tốt cho sức khoẻ và là một vị thuốc đông y. Hàm lượng dinh dưỡng phổ biến trong rau tần ô rất đa dạng: các loại vitamin A, B, C, E và K, chất kẽm, chất xơ, tinh dầu thơm.', 'https://dalatfarmtravel.com/wp-content/uploads/2020/10/tan-o.jpg', 24000, 'Nhật Bản', 48, 58, 344, '100g~300g', 45000, 2),
(69, 'Cần tây hữu cơ', 'Đối với người đang ăn kiêng, ăn theo chế độ thì bông cải xanh là một thực phẩm không thể thiếu trong mỗi bữa ăn.', 'https://minhcaumart.vn/media/com_eshop/products/2900531.jpg', 18000, 'Úc', 45, 141, 573, '100g~300g', 48000, 2),
(70, 'Cải bó xôi hữu cơ', 'Trong cải bó xôi chứa nhiều chất dinh dưỡng như beta-carotene, vitamin C và E, canxi, phospho và một lượng sắt nhất định, cung cấp nhiều chất dinh dưỡng cho cơ thể, giúp thúc đẩy tăng trưởng, tăng cường sức đề kháng, ngăn ngừa lão hóa, làm sạch da. Cải bó xôi có thể chế biến thành nhiều món ăn: Xào tỏi, nấu canh, nấu cháo, ăn lẩu, trộn salad…', 'https://salt.tikicdn.com/cache/750x750/ts/product/dc/82/94/bbfab04ffc9cac1bbb1a02faa89b77c5.jpg.webp', 22000, 'Brazil', 40, 111, 678, '100g~300g', 50000, 2),
(71, 'Bông cải xanh', 'Bông cải xanh (súp lơ xanh) là một loại cây thuộc loài , có hoa lớn ở đầu, thường được dùng như . Bông cải xanh thường được chế biến bằng cách luộc hoặc hấp, nhưng cũng có thể được ăn sống', 'https://salt.tikicdn.com/cache/750x750/ts/product/f6/56/93/b3bb4144402d697feea49e283688707f.jpg.webp', 15000, 'Việt Nam', 41, 126, 342, '100g~300g', 20000, 2),
(72, 'Đậu bắp', 'Đậu bắp là loại thực phẩm quen thuộc thường thấy trong các bữa cơm gia đình của người Việt. Là một loại thực phẩm giàu dinh dưỡng, đậu bắp còn tốt cho tiêu hóa, tốt cho thận, chống ung thư, tăng cường miễn dịch,… Sản phẩm được trồng bằng công nghệ hiện đại, đảm bảo tươi sạch, đảm bảo vệ sinh an toàn thực phẩm.', 'https://vinmec-prod.s3.amazonaws.com/images/20210604_012542_237817_qua-dau-bap.max-1800x1800.jpg', 23000, 'Úc', 49, 136, 267, '100g~300g', 28000, 2),
(73, 'Ngò gai (mùi tàu)', 'Ngò gai là một loại rau gia vị phổ biến của người Việt, ngoài là nguyên liệu chế biến món ăn, ngò gai còn có tác dụng chữa bệnh hiệu quả. Sản phẩm đạt tiêu chuẩn VietGAP, không chứa các hóa chất, chất kích thích tăng trưởng gây hại, đảm bảo an toàn cho sức khỏe người tiêu dùng. Do đó, bạn hoàn toàn có thể an tâm khi lựa chọn sản phẩm này trong mỗi bữa ăn của gia đình mình, giúp bữa ăn trở nên ngon miệng hơn.', 'https://salt.tikicdn.com/cache/750x750/ts/product/dd/ff/88/db1cb0d7710e75d959ccbe5c2a9e39fd.jpg.webp', 22000, 'Hàn Quốc', 44, 123, 323, '100g~300g', 27000, 2),
(74, 'Cải thìa', 'Cải thìa là một loài cải thuộc họ cải cùng họ với cải thảo, cải bẹ xanh. Cải Thìa là loại rau rất gần gũi với các món ăn của người Việt Nam. Cải Thìa chứa nhiều chất xơ thiết yếu cho quá trình tiêu hóa và giúp phòng ngừa bệnh táo bón. Chất Beta carotene dồi dào có trong Cải Thìa sẽ mang lại nhiều lợi ích cho đôi mắt của bạn. Nó có tác dụng phòng ngừa bệnh đục nhân mắt và thoái hóa hoàng điểm ở mắt.', 'https://salt.tikicdn.com/cache/750x750/ts/product/c8/c4/6c/b68dd02e6277f04569a5960b29b983d4.jpg.webp', 26000, 'Úc', 36, 143, 291, '100g~300g', 41000, 2),
(75, 'Hành Lá', 'Hành lá hay hành xanh, hành non là tên gọi chung của các loài thuộc chi Hành. Tất cả hành lá đều có lá rỗng màu xanh, nhưng lại thiếu một thân hành phát triển hoàn chỉnh. Chúng được trồng bởi hương vị thơm dịu hơn so với hầu hết hành tây và được ăn sống hoặc nấu chín như một loại rau.', 'https://thucphamdongxanh.com/wp-content/uploads/2019/09/hanh-la-coop-tinh-kg-sieu-thi-coop-food-cau-ong-dau-600x600.jpg', 30000, 'Pháp', 43, 72, 678, '100g~300g', 56000, 2),
(76, 'Giá Sống', 'Giá đỗ là một loại rau mầm khá phổ biến ở Việt Nam với cách trồng đơn giản, thời gian sinh trưởng nhanh chóng. Giá đỗ được mệnh danh là thực phẩm vàng cho làn da da phụ nữ. Do giá đỗ chứa rất nhiều dinh dưỡng như: protein thực vật, vitamin nhóm B, vitamin C, E, K.', 'https://cdn.tgdd.vn/Products/Images/8820/232789/bhx/gia-song-tui-200g-202012282232539599.jpg', 20000, 'Brazil', 42, 136, 842, '100g~300g', 29000, 2),
(77, 'Cải Thảo', 'Bắp cải thảo còn được gọi là cải bao, cải cuốn, cải bắp tây, là phân loài thực vật thuộc họ Cải ăn được có nguồn gốc từ Trung Quốc, được sử dụng rộng rãi trong các món ăn ở Đông Nam Á và Đông Á.', 'https://salt.tikicdn.com/cache/750x750/ts/product/e5/d3/bd/c3f92d8a50d085aee3cdf21dac6825d9.jpg.webp', 29000, 'Pháp', 46, 75, 437, '100g~300g', 53000, 2),
(78, 'Cà Chua', 'Cà chua là một trong những loại rau quả có giá trị dinh dưỡng cao, nhất là vitamin A, vitamin C và vitamin K, cùng với các hàm lượng chất khoáng khác như phốt pho, mangan, magiê, kali, đồng,….', 'https://minhcaumart.vn/media/com_eshop/products/c%20chua%20sach%20an%20ton.jpg', 21000, 'Pháp', 50, 125, 898, '100g~300g', 48000, 2),
(79, 'Chanh không hạt', 'Chanh không hạt có vỏ mỏng, mọng nước, có vị chua thanh và có mùi thơm. Trái chanh sau hái chỉ cần bảo quản trong điều kiện bình thường cũng để được rất lâu, vỏ xanh tươi. Trong chanh có rất nhiều vitamin C và các khoáng chất có lợi cho sức khỏe của con người.', 'https://minhcaumart.vn/media/com_eshop/products/chanh.jpg', 17000, 'Pháp', 37, 84, 427, '100g~300g', 31000, 2),
(80, 'Rau Dền Cơm Baby Hữu Cơ', 'Rau dền cơm baby hữu cơ vốn chứa tính mát, dễ ăn và rất thích hợp với mọi gia đình. Bởi nó không chỉ mang lại nhiều lợi ích bất ngời với sức khỏe mà còn rất giàu dinh dưỡng, được dùng để chế biến các món ăn hằng ngày trong bữa cơm gia đình.', 'https://product.hstatic.net/1000246697/product/rau_den_com_baby_4d7983768ed0478195899b8af21a0b36_master.jpg', 19000, 'Nhật Bản', 42, 137, 638, '100g~300g', 49000, 2),
(81, 'Khổ Qua Hữu Cơ', 'Khổ qua hữu cơ từ lâu được biết đến là một loại rau ăn quả cung cấp nhiều dưỡng chất mà khó có loại rau củ nào bì được. Sở hữu vị đắng nhưng nó chắc chắn là một nguồn cung cấp những dưỡng chất thiết yếu cho cơ thể như calorie, nước, carbohydrate, chất xơ, vitamin C, sắt, vitamin A cùng các chất chống oxy hóa.', 'https://csfood.vn/wp-content/uploads/2018/03/Kh%E1%BB%95-qua-VietRat.jpg', 25000, 'Pháp', 42, 145, 329, '100g~300g', 31000, 2),
(82, 'Ngò Rí Hữu Cơ', 'Ngò rí được biết là một loại rau thơm vô cùng quen thuộc trong các bữa ăn hàng ngày. Nhưng bạn có biết ngò rí hữu cơ lại có giá trị dinh dưỡng rất lớn cho sức khỏe.', 'https://photo-cms-giacngo.zadn.vn/w950/UserImages/2019/07/13/11/ngori.jpg', 30000, 'Úc', 37, 146, 385, '100g~300g', 36000, 2),
(83, 'Khoai tây vàng', 'Khoai Tây Vàng Đà Lạt có kích thước nhỏ hơn khoai tây thường, ruột vàng, khi luộc dễ chín, ăn vừa miệng. Khoai có tính bột cao, ngọt, có thể chế biến thành nhiều món, trong đó có làm bánh. Khoai tây còn là nguồn cung cấp dồi dào các vitamin B6, B2, B3, magiê, kẽm, sắt, chất xơ, chất chống oxy hóa.', 'https://salt.tikicdn.com/cache/750x750/ts/product/8e/de/91/8199a386d52ce432aba54e7b2c436668.jpg.webp', 22000, 'Brazil', 43, 62, 386, '100g~300g', 38000, 2),
(84, 'Bí giống Mỹ (bí cô tiên)', 'Bí giống Mỹ (bí cô tiên) có tính thanh nhiệt, nhuận tràng nhờ chất xơ kích thích nhu động ruột. Bí có hàm lượng dinh dưỡng rất cao. Trong thịt bí đỏ chín có sắt, vitamin C, kali, photpho, nước, protein thực vật, 8% Ngoài ra còn có các vitamin khác như B1, B2, B5, B6, PP và các axit béo linoleic, linolenic.', 'https://salt.tikicdn.com/cache/750x750/ts/product/3d/4f/7f/104cd1ccc843cd4ba72d1967bd17c14e.jpg.webp', 15000, 'Pháp', 41, 97, 885, '100g~300g', 31000, 2),
(85, 'Tía tô hữu cơ', 'Rau tía tô là loại rau thơm rất quen thuộc được trồng phổ biến và có mặt trong nhiều món ăn hằng ngày của người Việt. Mùi thơm đặc trưng, ngoài làm gia vị trong các món ăn ra thì tía tô còn có nhiều công dụng phòng chữa bệnh như cảm cúm, ho, chống viêm, đầy hơi trướng bụng, an thai, làm đẹp.', 'https://salt.tikicdn.com/cache/750x750/ts/product/c9/14/10/ec2b1314ff2c34e140f866e27c1230ff.jpg.webp', 30000, 'Hàn Quốc', 46, 147, 306, '100g~300g', 44000, 2),
(86, 'Hành tím', 'Hành tím là loài cây thân thảo, có củ to tròn, vỏ bên ngoài màu tím đậm. Hành tím thường được chế biến để ăn sống trong món salad, bánh mì hay món nhúng.', 'https://csfood.vn/wp-content/uploads/2020/04/H%C3%A0nh-T%C3%ADm-C%E1%BB%A7-VietRAT-T%C3%BAi-500g.png', 15000, 'Việt Nam', 39, 123, 251, '100g~300g', 33000, 2),
(87, 'Xả hữu cơ', 'Xả là một gia vị tuyệt vời cho bữa ăn thêm thơm ngon hoàn hảo, trọn vẹn hơn, không chỉ vậy nó còn là một vị thuốc nhiều tác dụng cho sức khỏe.', 'https://sieuthiandam.com/wp-content/uploads/2020/07/product6943.jpg', 29000, 'Pháp', 45, 108, 794, '100g~300g', 37000, 2),
(88, 'Rau muống', 'Rau muống là loại rau xanh phổ biến được sử dụng hầu hết trong bữa ăn của các gia đình Việt đồng thời nó cũng có những công dụng ít ai biết như tốt cho tim mạch.', 'https://minhcaumart.vn/media/com_eshop/products/RAUMUONG.jpg', 22000, 'Nhật Bản', 33, 57, 278, '100g~300g', 27000, 2),
(89, 'Cải xoăn', 'Cải xoăn hay còn gọi là cải xoắn là loại rau ăn lá có màu xanh đậm với nhiều dưỡng chất tốt cho sức khỏe. Cải xoăn có đặc tính chống ung thư cao, như chứa các dưỡng chất thực vật glucosinolat. Điều này có thể giúp tránh phát triển các khối u cũng như ngăn chặn các enzym có liên quan đến ung thư.', 'https://dalatfarm.net/wp-content/uploads/2020/10/cai-xoan-kale.jpg', 17000, 'Úc', 46, 144, 513, '100g~300g', 41000, 2),
(90, 'Bắp cải tim', 'Bắp cải trái tim là một giống rau mới được trồng nhiều. Bắp cải trái tim có kích cỡ nhỏ hơn so với bắp cải thông thường, khi cắt làm đôi có dạng trái tim. Bắp cải tím có chứa một hàm lượng dồi dào các vitamin cần thiết cho cơ thể như vitamin K, canxi, magie, mangan và nhiều loại khoáng chất.', 'https://csfood.vn/wp-content/uploads/2016/05/B%E1%BA%AFp-c%E1%BA%A3i-tr%C3%A1i-tim-%C4%90%C3%A0-L%E1%BA%A1t-1.jpg', 22000, 'Việt Nam', 40, 70, 855, '100g~300g', 47000, 2),
(91, 'Rau má hữu cơ', 'Rau má không chỉ là một loại rau thông dụng, có thể ăn hàng ngày mà còn là một vị thuốc chữa được nhiều chứng bệnh như: mụn nhọt, hạ sốt, làm đẹp, tăng cường sức khỏe, giải độc, táo bón, tim mạch. Rau má có thể ăn sống hoặc nấu canh, luộc hay giã nhuyễn vắt lấy nước uống.', 'https://vinmec-prod.s3.amazonaws.com/images/20210602_144348_507977_rau-ma-tac-dung.max-1800x1800.jpg', 19000, 'Hàn Quốc', 46, 105, 291, '100g~300g', 47000, 2),
(92, 'Bông cải trắng', 'Bông cải trắng hay còn gọi là súp lơ trắng là loại cây có phần bông trắng khít, xốp và mềm, bên ngoài có lá bao bọc xung quanh, phiến lá dày, cứng, phần bông được dùng để ăn còn phần lá dành cho gia súc.', 'https://salt.tikicdn.com/cache/750x750/ts/product/49/7d/b5/484cb1de9777376ef0e0a4515cbea9f4.jpg.webp', 24000, 'Brazil', 41, 89, 823, '100g~300g', 39000, 2),
(93, 'Cải ngồng hữu cơ', 'Cải ngồng có thể dùng để luộc chấm mắm tỏi hoặc mắm trứng hay chế biến các món xào : cải ngồng xào thịt bò, cải ngồng xào tỏi, canh cải ngồng cá thát lác… Các món này rất dễ làm và không ngán nhờ vị ngon ngọt tự nhiên của cải ngồng.', 'https://product.hstatic.net/200000423303/product/cai-ngong-huu-co_7040d71c8df64334be916ea10db3d897_grande.jpg', 25000, 'Hàn Quốc', 39, 86, 462, '100g~300g', 54000, 2),
(94, 'Rau lang hữu cơ', 'Rau lang là một loại rau đơn giản, dân dã, được dùng rất phổ biến trong các bữa ăn. Rau lang là món ăn rất ngon & bổ dưỡng thường được dùng để xào tỏi, xào thịt bò, xào lòng gà, luộc hoặc nấu canh.', 'https://salt.tikicdn.com/cache/750x750/ts/product/a7/80/8a/0fd50f890ead64c4661a48292de2144d.jpg.webp', 23000, 'Hàn Quốc', 30, 51, 220, '100g~300g', 52000, 2),
(95, 'Xà Lách Hữu Cơ', 'Trong rau xà lách chứa nhiều dinh dưỡng, giàu chất xơ, beta caroten, nhiều vitamin, thường dùng để ăn rau sống, trộn salad, trộn thịt bò chua ngọt, thịt nướng cuộn xà lách, canh xà lách thịt bằm vô cùng thơm ngon và hấp dẫn hãy bổ sung vào thực đơn cho gia đình nhé.', 'https://salt.tikicdn.com/cache/750x750/ts/product/1f/4d/dc/f4e28184e5a95f09f2f199f1a98a3ea1.png.webp', 16000, 'Brazil', 34, 59, 351, '100g~300g', 25000, 2),
(96, 'Mướp hương hữu cơ', 'Mướp là một loại cây thảo dạng dây leo. Trong quả mướp có chứa nhiều nước, protid, lipid, glucid, xenlulo, canxi, photpho, sắt, beta-caroten, B1, B6, B2, C. Mướp thường được chế biến thành món xào chung với thịt heo, lòng gà hoặc nấu canh rất bổ dưỡng.', 'https://salt.tikicdn.com/cache/750x750/ts/product/7e/a5/3f/d8ae39eaf4a53f8636f1cbf624c5a085.png.webp', 25000, 'Hàn Quốc', 41, 128, 744, '100g~300g', 40000, 2),
(97, 'Củ dền hữu cơ', 'Củ dền là một loại củ cũng rất quen thuộc trong nhiều món ăn của người Việt. Ngoài vị ngọt rất đặc trưng, củ dền còn là một loại củ rất bổ dưỡng. Củ dền có thể được chế biến bằng cách luộc, nướng, nấu canh, nấu súp, làm  sau khi nấu và thêm dầu và , nấu chè hoặc cũng có thể ăn sống, ép nước.', 'https://salt.tikicdn.com/cache/750x750/ts/product/b6/0c/d9/ac80365f84182841825dcd9345148dc5.png.webp', 20000, 'Hàn Quốc', 41, 60, 557, '100g~300g', 44000, 2),
(98, 'Dưa leo baby hữu cơ', 'Dưa leo có vị ngọt, tính mát, hương vị giòn ngon hấp dẫn và đặc biệt chứa rất nhiều nước, chính vì vậy từ lâu nó đã trở thành thực phẩm quen thuộc trong bữa ăn hằng ngày của mỗi gia đình.', 'https://salt.tikicdn.com/cache/750x750/ts/product/f5/b0/35/ccf72a5415bdb6b4e162c6d1d8a525dd.jpg.webp', 27000, 'Brazil', 48, 149, 870, '100g~300g', 56000, 2),
(99, 'Cà rốt baby hữu cơ', 'Cà rốt là một loại cây có củ, thường có màu đỏ, vàng, trắng hay tía. Phần ăn được của cà rốt là củ, thực chất là rễ cái của nó, chứa nhiều tiền tố của vitamin A tốt cho mắt.', 'https://photo-cms-tpo.epicdn.me/w890/Uploaded/2022/rwbvhvobvvimsb/2021_09_26/cacc80-rocc82cc81t-8919.jpg', 15000, 'Úc', 49, 115, 745, '100g~300g', 20000, 2),
(100, 'Khoai lang Nhật vàng hữu cơ', 'Khoai lang không chỉ là thực phẩm quen thuộc trong dân gian, mà lá và củ còn là vị thuốc trị nhiều bệnh. Trong Đông y, khoai lang có tính bình, vị ngọt, có tác dụng bồi bổ cơ thể, ích khí, cường thận, kiện vị, tiêu viêm, thanh can, lợi mật, sáng mắt.', 'https://salt.tikicdn.com/cache/750x750/ts/product/20/24/59/5f657c7afeabe2fbf393ac1e47e48f9b.jpg.webp', 28000, 'Úc', 48, 105, 285, '100g~300g', 33000, 2),
(101, 'Cà tím hữu cơ', 'Cà tím có tác dụng chữa bệnh rất tốt: phòng ngừa ung thư, giúp không tăng cân, tốt cho làn da và tóc, tăng cường sức khỏe tim mạch. Cà tím có thể chế biến được rất nhiều món ăn như: cà tím nhồi, cà nướng, cà om, salad, cà tím xào chay, cà tím nướng phô mai…', 'https://salt.tikicdn.com/cache/750x750/ts/product/8b/27/69/0dd45654245e3cb8e2446e41f22b14f3.jpg.webp', 19000, 'Việt Nam', 35, 58, 846, '100g~300g', 25000, 2),
(102, 'Khoai mỡ', 'Khoai mỡ không chỉ là một món ăn dân dã mà còn được xem là vị thuốc quý có lợi cho con người có nhiều công dụng đối với sức khỏe. Khoai mỡ có thể dùng trong nhiều món ăn quen thuộc như luộc, chiên hay nấu canh, hấp bánh và mang lại khá nhiều lợi ích cho sức khỏe.', 'https://salt.tikicdn.com/cache/750x750/ts/product/ff/29/5d/2ba265d5fbb9cdce612917db4214f667.jpg.webp', 30000, 'Nhật Bản', 34, 72, 700, '100g~300g', 43000, 2),
(103, 'Bí đao hữu cơ', 'Bí đao (bí xanh) là loại thức ăn mát, bổ, rẻ, rất thông dụng ở Việt Nam. Trong bí đao chứa nhiều nước, nhiệt lượng thấp, không có chất béo. Bí đao thường dùng nấu, xào, luộc. Bí đao còn được dùng thắng với làm mứt bí.', 'https://salt.tikicdn.com/cache/750x750/ts/product/21/0b/e4/267cf3d7a2451f79d966392556c87a23.jpg.webp', 20000, 'Hàn Quốc', 43, 142, 855, '100g~300g', 45000, 2),
(104, 'Dừa Xiêm Gọt Vỏ', 'Dừa xiêm gọt vỏ có trái to, nhiều nước. Nước dừa có vị ngọt thanh, đậm đà. Dừa là loại trái cây có tính mát, được rất nhiều người ưa chuộng và lựa chọn sử dụng. Dừa có cạnh thấp, mịn, màu đều là dừa nhiều nước.', 'https://salt.tikicdn.com/cache/400x400/ts/product/24/55/2d/4118b8cd5f8499e2cb041f00b5d07984.png', 15000, 'Việt Nam', 42, 95, 762, '1 trái~3 trái', 17000, 1),
(105, 'Cam Vàng ', 'Cam nhập khẩu đạt tiêu chuẩn xuất khẩu toàn cầu. Bảo quản trái cây tươi ngon đến tận tay khách hàng. Cam vàng ngon nhất khi chín, còn tươi, vỏ cam màu vàng đậm, bóp nhẹ vỏ thấy dầu còn tiết ra ngoài. Múi cam to, tép mọng nước, vị chua dịu, thích hợp để vắt nước.', 'https://product.hstatic.net/200000040878/product/upload_6e8f776d9006470092808520efdaf4e3.jpg', 26000, 'Pháp', 46, 141, 800, '500g~1 kg', 28000, 1),
(106, 'Lựu Hạt Mềm ', 'Lựu có vị ngọt tự nhiên, hạt mềm và màu đỏ rất thu hút. Lựu là trái cây chứa rất nhiều dinh dưỡng đem lại nhiều lợi ích cho sức khỏe.', 'https://vinmec-prod.s3.amazonaws.com/images/20200710_075654_574479_dd82a09a46dcaf82f6c.max-1800x1800.jpg', 28000, 'Việt Nam', 43, 66, 667, '500g~1 kg', 31000, 1),
(107, 'Lê đường', 'Lê đường là trái cây nhập khẩu Nhật Bản được nhiều người Việt Nam ưa chuộng. Lê đường hộp có vị ngọt đậm hơn lê đá, nhiều nước và thịt ít sạn cát hơn nên khi ăn cảm thấy mềm và xốp có màu vàng tươi, rất bắt mắt.', 'https://bizweb.dktcdn.net/100/010/432/products/23fb0960df78333dbc26a6d3e0a46356.jpg?v=1530625676233', 45000, 'Nhật Bản', 50, 76, 841, '500g~1 kg', 49000, 1),
(108, 'Táo Ninh Thuận', 'Táo là trái cây có hình dáng khá tròn, nhỏ, giòn và có vị ngọt thanh. Táo chỉ cần rửa sạch, chấm với muối ớt hoặc muối tôm ăn bắt vị vô cùng. Táo chín vỏ táo ngả dần sang  màu phớt vàng, quả chắc sẽ rất ngọt và giòn. Táo Ninh Thuận còn rất nhiều lợi ích cho sức khỏe.', 'https://www.thfruit.vn/wp-content/uploads/2021/09/Tao-Xanh-2-scaled.jpg', 32000, 'Việt Nam', 34, 66, 586, '500g~1 kg', 35000, 1),
(109, 'Cam Sành', 'Cam sành là loại trái cây được nhiều người yêu thích. Cam sành trái to, cầm chắc tay, khi ăn sẽ cảm nhận được vị chua chua ngọt ngọt, giàu vitamin C tốt cho cơ thể. Cam sành ngon nhất khi vỏ cam có màu xanh đậm, sần nhẹ, cuống nhô lên thì sẽ ngọt thanh hơn.', 'https://product.hstatic.net/200000401005/product/1_a165d3f13992458385d397ed75124b04_master.png', 18000, 'Nhật Bản', 48, 114, 747, '500g~1 kg', 19000, 1),
(110, 'Nho Đỏ', 'Nho đỏ có phần thịt dày mọng nước, vị ngọt xen lẫn chua nhẹ hài hoà, là loại trái cây được nhiều người yêu thích vào những ngày hè. Loại nho đỏ  mang lại nhiều lợi ích đối với sức khoẻ, nho ngọt khi có vỏ màu đỏ tím, đậm, chín đều, chắc, mọng.', 'https://www.thfruit.vn/wp-content/uploads/2021/09/MS00453-1.jpg', 26000, 'Nhật Bản', 44, 145, 700, 'hộp 100g~hộp 500g', 35000, 1),
(111, 'Chuối Sứ Nải', 'Chuối sứ là trái cây chứa rất nhiều chất dinh dưỡng cần thiết cho cơ thể, hương vị bùi bùi ngọn dịu vô cùng hấp dẫn. Ngoài ăn trực tiếp chuối sứ còn có thể dùng chế biến nhiều món ăn ngon hấp dẫn như sinh tố, chè, bánh, kem, sữa,...Chuối ngon nhất khi bắt đầu xuất hiện đốm nâu.', 'https://citifruit.com/uploads/images/Products/60/Chuoi-Su-800%C3%97800.jpg', 27000, 'Việt Nam', 42, 143, 459, 'nải 1 kg', 30000, 1),
(112, 'Chuối Nhập Khẩu', 'Loại chuối già được nhiều khách hàng ưa chuộng. Chuối chứa nhiều chất dinh dưỡng như kali, chất xơ, vitamin,... Chuối ăn ngon nhất khi chín vàng, trên vỏ bắt đầu có đốm nâu, khi đó chuối sẽ rất ngọt...Cam kết đúng khối lượng, bao bì kín đáo, an toàn và bảo đảm hợp vệ sinh.', 'http://cayhoacanh.com/wp-content/uploads/2015/07/14726hinh-nai-chuoi-nho.jpg', 28000, 'Hàn Quốc', 48, 108, 773, 'Hộp 6 trái', 31000, 1),
(113, 'Chanh Dây', 'Chanh dây là loại trái cây dinh dưỡng, có vị chua chua, ngọt ngọt. Chanh dây ngon nhất khi vỏ càng đậm, hơi nhăn nhẹ, vị ngon ngọt hơn nhiều. Chanh dây cỏ thể dùng để chế biến các món như nước ép chanh dây, sinh tố chanh dây,...', 'https://media.cooky.vn/images/blog-2016/bat-mi-cong-dung-lam-dep-toan-dien-cua-chanh-day-1.jpg', 16000, 'Brazil', 40, 110, 482, '500g~1 kg', 18000, 1),
(114, 'Nho Da Xanh', 'Từ vùng trồng nho nổi tiếng cả nước, nho xanh quả to, tươi ngon, màu sắc đẹp, chứa nhiều vitamin, nho ngọt khi trái to, xanh sáng, căng mọng. Trái cây cam kết bán đúng khối lượng, chất lượng và an toàn tuyệt đối. Bao bì kín đáo, sạch sẽ và hợp vệ sinh.', 'https://bizweb.dktcdn.net/100/010/432/products/green-grape.jpg?v=1530625676553', 48000, 'Hàn Quốc', 44, 84, 590, 'hộp 100g~hộp 500g', 52000, 1);
INSERT INTO `tb_product` (`id`, `name`, `desc`, `img`, `price`, `location`, `star`, `review`, `sold`, `unit`, `old_price`, `id_category`) VALUES
(115, 'Vú Sữa', 'Vú sữa là một loại quả ngon rất được nhiều người ưa thích. Quả vú sữa có hàm lượng chất dinh dưỡng cao, hương vị rất ngọt và bổ dưỡng. Trong quả vú sữa có chứa lượng Canxi rất dồi dào, không chỉ tốt cho sự phát triển của răng và xương mà còn ngăn ngừa chuột rút.', 'https://product.hstatic.net/200000240163/product/vusuatrang_65557092ba8e4c69a9d98e26ca283d90_master.jpg', 90000, 'Việt Nam', 44, 92, 629, '500g~1 kg', 100000, 1),
(116, 'Kiwi', 'Kiwi là một trong những loại trái cây nhập khẩu 100% nói không với chất bảo quản.Kiwi là loại trái cây có nhiều hương vị tuy kích thước khá nhỏ và rất nhiều lợi ích cho sức khỏe. Thịt xanh của chúng thơm và ngọt. Chúng cũng chứa đầy các chất dinh dưỡng. Ngoài ra, chúng cũng có rất nhiều chất chống oxy hóa và là nguồn cung cấp dồi dào chất xơ.', 'https://www.producemarketguide.com/sites/default/files/Commodities.tar/Commodities/kiwifruit_commodity-page.png', 160000, 'Nhật Bản', 42, 141, 813, 'hộp 100g~hộp 500g', 180000, 1),
(117, 'Nho Đen', 'Nho đen không hạt là loại hoa quả nhập khẩu rất giàu dinh dưỡng, có tác dụng bổ tì, ích khí, giúp cơ thể khỏe mạnh, miễn dịch tốt và làm chậm quá trình lão hóa.', 'https://minhcaumart.vn/media/com_eshop/products/9900000480516.jpg', 132000, 'Pháp', 44, 99, 897, 'hộp 100g~hộp 500g', 140000, 1),
(118, 'Bơ Booth', 'Bơ Booth là một giống bơ cho năng suất cao và chất lượng ngon được ưa chuộng trên thị trường. Hiện đang được bà con trồng phổ biến ở các tỉnh Tây Nguyên.', 'https://dalatfarm.net/wp-content/uploads/2021/10/bo-booth-da-lat.jpg', 65000, 'Úc', 35, 83, 469, 'hộp 100g~hộp 500g', 80000, 1),
(119, 'Bơ Sáp', 'Bơ Sáp là giống bơ nhập và được trồng rộng rãi ở vùng Tây Nguyên nước ta. Với đặc tính thịt béo và chứa nhiều sáp nên rất được ưa chuộng.', 'https://grovefresh.vn/Data/Sites/1/Product/6496/3030576_b%C6%A1-s%C3%A1p-gf---10212.jpg', 40000, 'Úc', 39, 113, 377, 'hộp 100g~hộp 500g', 50000, 1),
(120, 'Dâu Tây', 'Dâu tây là một loại trái cây được rất nhiều người ưa chuộng. Dâu tây được trồng nhiều ở một số khu vực có khí hậu mát mẻ, thuận lợi. Dâu tây là một nguồn cung cấp chất chống oxy hóa và chất dinh dưỡng có tác dụng bảo vệ sức khỏe mạnh mẽ.', 'https://dalatfarm.net/wp-content/uploads/2020/12/dau-tay-da-lat.jpg', 140000, 'Pháp', 41, 136, 792, 'hộp 100g~hộp 500g', 150000, 1),
(121, 'Mâm Xôi', 'Quả mâm xôi hay còn gọi là phúc bồn tử được biết đến là một loại trái có hương vị thơm ngon, không chỉ vậy còn có công dụng trị tiểu đường, kháng khuẩn, chống lão hóa, làm đẹp da,….', 'https://suckhoedoisong.qltns.mediacdn.vn/Images/bichvan/2016/10/29/loi-ich-suc-khoe-cua-qua-mam-xoi.jpg', 16000, 'Pháp', 46, 128, 642, 'hộp 100g~hộp 500g', 18000, 1),
(122, 'Trái Cherry', 'Cherry vỏ đỏ căng bóng, mọng nước, thịt trái chắc, giòn, có vị ngọt xen chút chua nhẹ, là loại trái cây ưa thích của nhiều người, đặc biệt là trẻ nhỏ. Cherry còn mang lại cho bạn rất nhiều lợi ích về sức khỏe mà bạn không ngờ tới.', 'http://familyfruits.com.vn/wp-content/uploads/2021/01/cherry-jumbo-new-zealand.jpg', 100000, 'Úc', 42, 144, 211, 'hộp 100g~hộp 500g', 120000, 1),
(123, 'Ổi Nữ Hoàng', 'Ổi Nữ Hoàng được mệnh danh là “Nữ Hoàng của các loại ổi” có dạng hình tròn, có 2 gân dọc theo trái, trọng lượng trung bình từ 350 - 400gr/quả. Giống ổi cho quả có thịt trắng, ăn giòn, vị ngọt, thơm, ít hạt, ruột rất nhỏ.', 'https://salt.tikicdn.com/cache/525x525/ts/product/84/48/02/e496c5a09734971d3df232e3eb630cf9.png', 25000, 'Hàn Quốc', 45, 86, 462, '200g~500g', 54000, 1),
(124, 'Sapoche', 'Sapoche hay còn gọi là hồng xiêm được biết đến là loại hoa quả có vị ngọt thanh, chứa nhiều vitamin và dưỡng chất hữu ích. Khi ăn hồng xiêm có mùi thơm, ngon ngọt và bùi, được rất nhiều người yêu thích.', 'https://grovefresh.vn/Data/Sites/1/Product/6374/hong-xiem.jpg', 23000, 'Hàn Quốc', 30, 51, 220, '100g~300g', 52000, 1),
(125, 'Dưa Lưới Giống Hà Lan', 'Dưa lưới giống Hà Lan được trồng theo công nghệ Israel với sự giám sát nghiêm ngặt đã tạo ra giống dưa lưới với độ đường cao, thịt chắc, ruột màu xanh, trái tròn đều, vị ngọt lâu hơn.', 'https://salt.tikicdn.com/cache/750x750/ts/product/89/d2/c4/3fc8393f5700d8c5794c38a11338c37d.png.webp', 16000, 'Brazil', 34, 59, 351, '1 trái', 25000, 1),
(126, 'Việt quất', 'Quả việt quất có vị ngọt, bổ dưỡng và cực kỳ phổ biến. Việt quất được coi là một siêu thực phẩm do chúng cung cấp lượng calo thấp và cực kỳ tốt cho sức khỏe. Chúng rất ngon và rất dễ chế biến nên được nhiều người yêu thích.', 'http://product.hstatic.net/1000282430/product/viet-quat_grande.jpg', 25000, 'Hàn Quốc', 45, 128, 744, 'hộp 100g~hộp 500g', 40000, 1),
(127, 'Nhãn Ido', 'Nhãn IDO là Giống Nhãn Thái Lan được du nhập về nước ta trồng những năm gần đây. Đây là Giống Nhãn rất phù hợp với vùng đất của nhiều địa phương, cho trái to, cơm dầy, hạt nhỏ, vỏ cứng, dễ thu hoạch và được thị trường ưa chuộng.', 'https://bizweb.dktcdn.net/100/324/966/products/nhanxuongcomvang-c1876ecb-51c1-4db5-942e-e84e6d998158.jpg?v=1624982757580', 20000, 'Hàn Quốc', 41, 60, 557, '200g~500g', 44000, 1),
(128, 'Khóm (thơm) nguyên trái', 'Dứa, thơm hay khóm là những tên gọi loại quả quen thuộc mà chúng ta vẫn thường hay sử dụng, không chỉ được biết tới thức ăn, thức uống thơm ngon mà còn được dùng thường xuyên trong việc chữa bệnh rất tốt cho sức khỏe.', 'https://i.pinimg.com/736x/0f/5e/e5/0f5ee587a0d7400de8c62f59bd9aca5d.jpg', 27000, 'Brazil', 48, 149, 870, '1 trái', 56000, 1),
(129, 'Bưởi da xanh Bến Tre', 'Bưởi là loại trái cây giàu vitamin và khoáng chất, không chỉ có tác dụng bồi bổ cho sức khỏe mà còn giúp làm đẹp và chữa trị một số bệnh. Những người sành ăn thường sẽ chọn loại bưởi da xanh để ăn bởi đây là giống bưởi ngon và rất nổi tiếng.', 'https://product.hstatic.net/200000281397/product/upload_23d2dccfc5d54319887e2cc627bb648b_grande.jpg', 15000, 'Việt Nam', 49, 115, 745, '1 trái', 20000, 1),
(130, 'Sầu Riêng Musangking', 'Sầu riêng là một loại trái cây lớn, có mùi khá nồng và nặng, nhưng cực kỳ giàu các chất dinh dưỡng, chẳng hạn như vitamin C, vitamin B, khoáng chất.', 'https://grovefresh.vn/Data/Sites/1/Product/12523/s%E1%BA%A7u-ri%C3%AAng-monthong.jpg', 28000, 'Úc', 48, 105, 285, '1 trái', 33000, 1),
(131, 'Dưa hấu đỏ Long An', 'Dưa Hấu Đỏ Long An thuộc giống dưa có hạt, mọng nước hơn và ngọt đậm. Dưa được trồng và thu hoạch đạt tiêu chuẩn an toàn thực phẩm, đảm bảo cho người tiêu dùng.', 'http://thainguyentrade.vn/upload/product/import3/San-pham-dua-hau-fruits-tt.jpg', 19000, 'Việt Nam', 35, 58, 846, '1 trái', 25000, 1),
(132, 'Mận An Phước', 'Mận An Phước là giống mận được ghép từ mắt của giống Thongsamsri Thái Lan với gốc mận xanh đường Việt Nam. Mận An Phước có vị rất ngon và hàm lượng dinh dưỡng cao không thua kém các loại trái cây khác. Mận có nhiều tác dụng tốt cho sức khoẻ như: tốt cho xương khớp, cải thiện trí nhớ, hỗ trợ tiêu hoá, ngăn ngừa ung thư.', 'https://salt.tikicdn.com/ts/tmp/b0/0a/8b/40f5455547ad928996d645857b246c6d.jpg', 30000, 'Việt Nam', 34, 72, 700, '200g~500g', 43000, 1),
(133, 'Măng cụt Vietgap', 'Măng cụt là một loại quả thơm ngon, rất giàu dinh dưỡng, đem lại nhiều lợi ích cho sức khỏe, được ví như “nữ hoàng trái cây\". Không chỉ vậy, măng cụt còn là một vị thuốc quý.', 'https://foody24h.com/assets/images/products/1625412906XcjudlHo.png', 20000, 'Việt Nam', 43, 142, 855, '200g~500g', 45000, 1),
(134, 'Sữa Hạnh Nhân Hạt Chia Không Đường Hữu Cơ', 'Sữa Hạt Hạnh Nhân Hạt Chia với thành phần 100% Organic nhập khẩu an toàn khi sử dụng lâu dài cho gia đình. Đây vốn là nguồn dinh dưỡng tuyệt vời, không sử dụng chất bảo quản và hoá chất độc hại đặc biệt phù hợp với mọi đối tượng đặc biệt rất tốt cho phụ nữ mang thai.', 'https://bizweb.dktcdn.net/100/139/060/products/hanh-nhan-hat-chia-khong-duong-1l.png', 28000, 'Hàn Quốc', 44, 118, 429, '500ml~1 lít', 113000, 9),
(135, 'Sữa gạo rang TH True Rice', 'Được làm từ các nguyên liệu hoàn toàn tự nhiên, đặc biệt có vị ngọt tự nhiên từ gạo, tốt hơn cho sức khỏe. Với hương vị thơm ngon, hấp dẫn, sản phẩm không chỉ giúp giải khát, cung cấp nước cho cơ thể tràn đầy sức sống, mà còn mang lại dinh dưỡng lành mạnh, tự nhiên từ gạo.', 'https://www.thmilk.vn/wp-content/uploads/2019/11/RICE-white-800x800.jpg', 43000, 'Úc', 42, 189, 307, '500ml~1 lít', 137000, 9),
(136, 'Đậu Hũ Chiên Bibigo', 'Các món ăn từ đậu hũ chiên khá đa dạng như đậu hũ chiên sả ớt, đậu hũ chiên nước mắm, đậu hũ chiên nhồi thịt. Bảo quản trong ngăn mát tủ lạnh. Nên nấu và thưởng thức theo đúng thời hạn sử dụng ghi trên bao bì.', 'https://product.hstatic.net/1000246697/product/8938508547067_78ac92b9dfcb4459af143152bb0fd5ad_1ada46c0fa634660839e51446aeb72da.jpg', 62000, 'Nhật Bản', 48, 234, 538, '1 gói', 93000, 11),
(137, 'Đậu Hũ Trắng', 'Đậu hũ trắng được chế biến thủ công theo công thức gia truyền hơn 20 năm. Đảm bảo sạch, không hóa chất độc hại. Vẫn đảm bảo được hương vị đặc trưng. Là một trong những món ăn phổ biến trong khẩu phần của người Châu Á, đậu phụ mang lại nhiều lợi ích cho sức khỏe và chăm sóc sắc đẹp.', 'https://cooponline.vn/wp-content/uploads/2021/07/dau-hu-trang.jpg', 72000, 'Úc', 47, 210, 675, '1 gói', 133000, 11),
(138, 'Đậu Hủ Non Vị Nguyên', 'Đậu hũ Vị Nguyên được làm từ nguyên liệu đậu nành tươi sạch, không biến đổi gen mang đến hương vị béo ngậy, thơm ngon thuần khiết. Đậu hũ non Vị Nguyên giàu chất đạm, ít năng lượng, có thể dùng cho người đang ăn kiêng, giảm cân, là loại đậu hũ không thể thiếu cho mọi nhà.', 'https://cdn.tgdd.vn/Products/Images/7461/206302/bhx/dau-hu-non-vi-nguyen-hop-280g-201909060836262560.JPG', 116000, 'Hàn Quốc', 42, 124, 259, '1 gói', 209000, 11),
(139, 'Nấm mèo sấy lạnh', 'Nấm Mèo Sợi (Nấm Mộc Nhĩ Thái Sợi) là nấm đã sấy lạnh và cắt sợi sẵn, tiện lợi hơn trong chế biến. Là một loại nấm ăn giúp bổ sung chất sắt dồi dào, thích hợp với những người bị thiếu máu sử dụng, đặc biệt là phụ nữ.', 'https://media.cooky.vn/images/blog-2016/nam-meo-kho.png', 82000, 'Hàn Quốc', 43, 250, 881, '1 gói', 145000, 11),
(140, 'Nấm kim châm', 'Nấm kim châm là một trong những loại nấm có giá trị dinh dưỡng và tốt cho sức khỏe. Với đặc điểm có vị ngọt thanh nên loại nấm này thường được lựa chọn để chế biến các món ăn như lẩu, nướng, xào thịt bò,...', 'https://product.hstatic.net/200000423303/product/nam-kim-cham-han-quoc_05050b0f8abf415cbb679c2d62cf05f4.jpg', 105000, 'Việt Nam', 49, 186, 631, '1 gói', 129000, 11),
(141, 'Phù trúc tươi Kyoto Yuba', 'Phù trúc tươi, hay còn gọi là váng đậu là sản phẩm làm từ đậu nành đảm bảo an toàn, chất lượng. Phù trúc thường được chế biến thành các món chay hoặc nhúng lẩu. Phù trúc dai dai, giòn giòn, bùi bùi vị đậu chắc chắn sẽ giúp bạn nấu được món ăn đa dạng, hấp dẫn cho gia đình.', 'https://salt.tikicdn.com/cache/750x750/ts/product/a0/61/95/1805f976cb722802440b241165ff904d.png.webp', 25000, 'Canada', 41, 243, 594, '1 gói', 79000, 11),
(142, 'Nấm Ngọc Châm Trắng', 'Nấm Ngọc Châm Nâu hay còn được gọi là nấm hải sản. Thường được dùng phổ biến trong các món lẩu, món nướng cực kỳ ngon miệng và thông dụng,... Là thực phẩm chứa nhiều giá trị dinh dưỡng và tốt cho sức khỏe.', 'https://csfood.vn/wp-content/uploads/2018/03/N%E1%BA%A5m-ng%E1%BB%8Dc-ch%C3%A2m-n%C3%A2u-rica-khay-150g......png', 86000, 'Canada', 44, 103, 354, '1 gói', 171000, 11),
(143, 'Nấm đùi gà', 'Nấm đùi gà hay còn có tên gọi khác như nấm bào ngư Nhật, đây là loại nấm có kích thước lớn và chứa nhiều dưỡng chất thiết yếu cho cơ thể con người. Những lợi ích của loại nấm đùi gà mang đến cho sức khỏe không những bổ sung được các dinh dưỡng mà còn thúc đẩy hỗ trợ điều trị một số bệnh.', 'https://thucphamsachgiatot.vn/image/cache/catalog/V-NAM-DUI-GA-700x700.jpg', 110000, 'Hàn Quốc', 41, 167, 520, '1 gói', 174000, 11),
(144, 'Nấm thủy tiên trắng', 'Nấm thủy tiên trắng Nhật Bản là một trong những loại nấm thuộc họ nấm thủy tinh, với màu trắng tinh khiết, gợi đến sự thuần khiết từ tự nhiên, nấm thủy tiên trắng luôn là loại nấm được thực khách lựa chọn. Nấm thủy tiên trắng có hương thơm của Nấm linh chi.', 'https://cdn.shopify.com/s/files/1/0617/2497/files/nam-linh-chi-trang_f714aa6a-9d11-49fd-ac37-0017607f42c7_480x480.png?v=1635501240', 40000, 'Việt Nam', 50, 246, 418, '1 gói', 137000, 11),
(145, 'Nấm Rơm Tươi', 'Nấm Rơm tươi là một trong các loại nấm ăn rất được ưa thích ở khắp 3 miền bởi độ ngon khó cưỡng và vị ngọt thanh bùi hấp dẫn. Nấm Rơm có thể dùng chế biến được rất nhiều món ăn ngon và là một món nấm ăn lẩu không thể thiếu trong các bữa tiệc.', 'https://salt.tikicdn.com/cache/750x750/ts/product/cd/52/9c/72e0bb81743ee59448db50847708bf75.jpg.webp', 97000, 'Úc', 45, 94, 319, '1 gói', 173000, 11),
(146, 'Nấm trùng thảo', 'Nấm trùng thảo là một trong những sản vật lạ ở Việt Nam. Được biết đến không chỉ bởi công dụng tốt cho sức khỏe mà quá trình hình thành cũng khá đặc biệt. Sở dĩ gọi “nấm trùng thảo” là sản vật lạ bởi: Mùa đông là ấu trùng - Mùa hè là thảo mộc.', 'https://salt.tikicdn.com/cache/750x750/ts/product/59/2e/24/566946b11c5696b22c3df0421af169f6.jpg.webp', 95000, 'Việt Nam', 42, 116, 378, '1 chai', 154000, 11),
(147, 'Nấm bào ngư xám', 'Nấm bào ngư xám có màu xám nâu, cuống trắng, dày, thịt chắc, ăn giòn, ngọt, hơi dai, mùi thơm đặc trưng. Đây là thực phẩm có giá trị dinh dưỡng khá cao, cung cấp một lượng đáng kể chất đạm, đường, bột, vitamin và khoáng chất nguồn gốc thực vật có lợi cho cơ thể con người.', 'https://salt.tikicdn.com/cache/750x750/ts/product/ed/6f/bc/80cbe7237c5a2e4c3e1c1e413f29f1c6.jpg.webp', 91000, 'Úc', 43, 54, 236, '1 gói', 191000, 11),
(148, 'Nước xoài chuối tự nhiên TH true JUICE', 'Được làm từ những loại xoài và chuối tự nhiên từ các vùng nguyên liệu chất lượng cao trong nước và thế giới, đem lại vị ngon và nguồn dưỡng chất hoàn toàn từ thiên nhiên. Dòng sản phẩm trái cây xay có cấu trúc sánh mịn, đem lại trải nghiệm mới lạ.', 'https://salt.tikicdn.com/cache/750x750/ts/product/f0/03/ba/d1f701ee25e64b0db554f6f0fe479e60.jpg.webp', 94000, 'Việt Nam', 39, 72, 160, '1 chai', 162000, 12),
(149, 'Nước táo tự nhiên TH true JUICE', 'Được làm từ những loại táo tự nhiên từ các vùng nguyên liệu chất lượng cao trong nước và thế giới, đem lại vị ngon và nguồn dưỡng chất hoàn toàn từ thiên nhiên. Dòng sản phẩm trái cây xay có cấu trúc sánh mịn, đem lại trải nghiệm mới lạ.', 'https://salt.tikicdn.com/cache/750x750/ts/product/a9/73/ef/540571a954ed25148e4293d323ccdd7e.jpg.webp', 28000, 'Pháp', 46, 125, 287, '1 chai', 126000, 12),
(150, 'Nước dừa xiêm tắc COCOXIM', 'Sự kết hợp tuyệt vời giữa nước dừa tươi nguyên chất Bến Tre cùng hương vị nhiệt đới từ nước ép tắc đem đến vị thơm ngon mát lành. Dừa Xiêm Tắc giúp bạn giải nhanh cơn khát và bổ sung các khoáng chất thiết yếu như cho cơ thể tiếp thêm năng lượng cho một ngày dài năng động.', 'https://salt.tikicdn.com/cache/750x750/ts/product/0d/e7/46/1904c3c0fd34ac04aaba9728c69a3801.jpg.webp', 45000, 'Việt Nam', 50, 201, 594, '1 hộp', 67000, 12),
(151, 'Nước Ép Trái Cây Bundaberg', 'Nước ép trái cây Bundaberg là một loại thức uống hoà quyện cùng vị chua ngọt của trái cây được lên men và cần thiết cho sức khoẻ của con người, đặc biệt là duy trì sắc đẹp và sự tươi trẻ, giúp tế bào cơ thể chống lại được tác nhân gây hại, tăng sức đề kháng.', 'https://store4u.org/wp-content/uploads/2020/03/N%C6%B0%E1%BB%9Bc-%C3%89p-Bundaberg-Apple-Cider-%C3%9Ac-375ml-Store4u.org_.jpg', 41000, 'Nhật Bản', 46, 145, 511, '1 chai', 127000, 12),
(152, 'Nước Ép Trái Cây Hỗn Hợp Rita', 'Là sự kết hợp vị chua của chanh dây, cam và vị ngọt từ thơm, xoài mang lại thức uống thơm ngon, giàu dinh dưỡng. Đắm mình trong hương vị nhiệt đới tươi mát, Nước Ép Trái Cây Rita cho ngày dài thêm hứng khởi và đầy năng lượng.', 'https://ritajuice.vn/wp-content/uploads/2021/07/N%C6%B0%E1%BB%9Bc-tr%C3%A1i-c%C3%A2y-h%E1%BB%97n-h%E1%BB%A3p-%C4%91%C3%B3ng-chai-350ml.jpg', 43000, 'Việt Nam', 47, 170, 377, '1 chai', 73000, 12),
(153, 'Nước Nước Lựu WoongJin', 'Xuất xứ từ Hàn Quốc với hương vị thơm ngon giúp bổ sung năng lượng và vitamin thiết yếu, cho cơ thể thanh nhiệt trong những ngày nắng nóng. Hương vị trái lựu tươi ngon, đã qua chọn lọc kỹ càng.', 'https://salt.tikicdn.com/cache/750x750/ts/product/c1/2b/e1/a6249954d456bc7b321e27e2e50c4103.jpg.webp', 74000, 'Hàn Quốc', 41, 213, 480, '1 chai', 111000, 12),
(154, 'Nước ép Việt Quất Hữu Cơ Nguyên Chất Lakewood', 'Với hương vị ngọt tự nhiên từ hơn 1,4kg trái việt quất tươi nguyên chất 100% trong mỗi chai, mang đến thức uống chua chua ngọt ngọt thơm ngon. Nước ép Việt Quất hữu cơ nguyên chất Lakewood giúp hỗ trợ tim, bảo vệ cơ thể, hỗ trợ đường tiêu hoá, giúp sáng da, tăng cường miễn dịch.', 'https://lzd-img-global.slatic.net/g/p/9c53948d852d9a6b3d45cdd77d3740c4.jpg_1200x1200q80.jpg_.webp', 124000, 'Canada', 44, 173, 582, '1 chai', 176000, 12),
(155, 'Nước Ép Quýt Malee', 'Nước Ép Quýt Malee được chiết xuất 100% tự nhiên từ những quả quýt chất lượng, mang đến hương vị tự nhiên, sảng khoái và tràn đầy năng lượng. Sản phẩm có vị chua ngọt tự nhiên, đặc biệt chứa hàm lượng Vitamin C cao giúp tăng cường hệ miễn dịch và tăng đề kháng cho cơ thể thêm khỏe mạnh.', 'https://ubgmart.com/storage/san-pham/cong-nghe/nuoc-ep-quyt-malee-1-lit.jpg', 77000, 'Hàn Quốc', 44, 254, 861, '1 chai', 166000, 12),
(156, 'Nước Ép Đào Georgias Natural Peach', 'Sản phẩm nước ép từ Georgias Natural sử dụng 100% trái cây tươi được trồng và sản xuất theo tiêu chuẩn châu Âu, được chứng nhận USDA ORGANIC. Những trái đào mọng nước được trồng ở những khu vườn hữu cơ tại Georgia trong môi trường tự nhiên và an toàn nhất.', 'https://salt.tikicdn.com/cache/750x750/ts/product/0b/b7/51/d1f9f5f9313535788cdf4c1de334310a.jpg.webp', 138000, 'Pháp', 45, 243, 594, '1 chai', 173000, 12),
(157, 'Nước ép Nho đỏ Gavioli', 'Nước Ép Nho Có Ga Gavioli xuất xứ từ Ý, loại thức uống mang giá trị dinh dưỡng cao, sản phẩm được làm từ những quả nho tự nhiên, chọn lọc kỹ với hương vị trái cây như đào, nho đỏ, nho xanh, cam,... mang đến hương vị ngọt ngào, hấp dẫn cho mỗi bữa tiệc hay họp mặt gia đình.', 'https://cdn.shopify.com/s/files/1/0563/5745/4002/products/7acc70800d8bc2aad7af00cd9178e2a7_800x.jpg?v=1648637057', 44000, 'Canada', 45, 99, 359, '1 chai', 68000, 12),
(158, 'Gạo thơm ST24 Co.op Finest', 'Gạo thơm ST24 Coop Finest có đặc tính hạt dài, trắng trong, cơm dẻo vừa và có hương thơm mùi lá dứa. Đặc biệt, khi để nguội cơm vẫn ngon, không bị cứng. Gạo thơm ST24 Coop Finest được sản xuất theo quy trình gạo sạch và chế biến theo dây chuyền hiện đại với công nghệ của Thụy Sĩ.', 'https://salt.tikicdn.com/cache/750x750/ts/product/6a/23/3e/1194d1014b7d8075d5cbc67b031681d8.jpg.webp', 145000, 'Pháp', 44, 130, 322, '2 kg~5 kg', 179000, 4),
(159, 'Gạo sushi japonica LOTUS', 'Gạo Nhật Sushi trồng từ giống Japonica, được dùng trong nhiều chuỗi nhà hàng Nhật. Cơm mềm, rất dẻo, ngọt đậm, không dính nhão, có mùi thơm nhẹ. Hạt gạo tròn đều, không tẩy trắng tạo mùi.', 'https://salt.tikicdn.com/cache/750x750/ts/product/56/b7/4c/16cb691b55204c26bc5d1fce0b7b6b3e.jpg.webp', 144000, 'Nhật Bản', 42, 140, 526, '2 kg~5 kg', 196000, 4),
(160, 'Gạo gạo lứt hữu cơ Hoa Nắng', 'Gạo gạo lứt hữu cơ hoa nắng giữ nguyên lớp cám gạo, giàu dinh dưỡng. Hạt gạo thon dài, màu nâu. Cơm dẻo, bùi, mềm cơm, có vị ngọt và mùi thơm đặc trưng. Đặc biệt hàm lượng vitamin B1 trong gạo giúp chuyển hóa hàm lượng tinh bột đường thành năng lượng để cơ thể dễ hấp thụ.', 'https://salt.tikicdn.com/cache/750x750/ts/product/48/27/88/a7a209c786a9135f7631e3be5b1257c1.png.webp', 43000, 'Hàn Quốc', 44, 188, 605, '2 kg~5 kg', 96000, 4),
(161, 'Bún khô SAFOCO', 'Bún khô Safoco với sợi bún dai, trắng mềm là một sản phẩm thơm ngon, lạ miệng được khuyên dùng nếu bạn muốn đổi món thêm hương vị ẩm thực mới cho gia đình. THành phần: Bột gạo, muối ăn, chất ổn định. Bảo quản nơi khô ráo, thoáng mát.', 'https://cdn.tgdd.vn/Products/Images/8163/80571/bhx/bun-kho-400grx20-2-700x467.jpg', 91000, 'Hàn Quốc', 48, 162, 519, '1 gói', 160000, 5),
(162, 'Bún Gạo Sa Giang', 'Bún gạo Sa Giang sợi tròn, nhỏ, màu trắng được là, từ gạo dinh dưỡng. Bún gạo rất đa dạng trong chế biến, có thể dùng cho các món nước, xào, trộn. Để nơi khô ráo, thoáng mát.', 'https://salt.tikicdn.com/cache/750x750/ts/product/94/1d/a0/d3fb513b9fd8bb21dabed9fe72963ebf.png.webp', 76000, 'Việt Nam', 45, 163, 459, '1 gói', 150000, 5),
(163, 'Mì trứng cao cấp Meizan', 'Mì Trứng Cao Cấp Meizan được làm từ bột mì thượng hạng. Sản phẩm sử dụng trứng gà tươi đã qua kiểm định và được sản xuất trên dây chuyền tự động, sử dụng công nghệ hiện đại. Nhờ vậy, sản phẩm rất được ưa chuộng nhờ sở hữu sợi mì dai ngon tự nhiên, an toàn cho sức khỏe của cả nhà.', 'https://concung.com/2022/02/55314-80177-large_mobile/mi-trung-cao-cap-meizan-500g-x-12-goi.jpg', 131000, 'Canada', 50, 129, 340, '1 gói', 175000, 5),
(164, 'Miến Yến Tiệc Phú Hương', 'Miến Gói Phú Hương Yến Tiệc Acecook có thành phần được làm từ đậu xanh, cho bạn bát miếng thơm ngon, bổ dưỡng. Sợi miến dai ngon, có độ trong đẹp mắt, kết hợp cùng nước dùng thơm ngon, nóng hổi sẽ cho bạn những trải nghiệm tuyệt vời mỗi khi thưởng thức.', 'https://cf.shopee.vn/file/9930caefa28e24a0bf05d7bbaab03c18', 118000, 'Pháp', 41, 59, 225, '1 gói', 169000, 5),
(165, 'Mì ống bán lứt hữu cơ penne Markal', 'Khác với những loại mì khác thường có màu trắng hay trắng đục, thì mì ống penne lứt hữu cơ Markal có màu nâu, dạng ống nhỏ. Mì ống penne lứt hữu cơ đã được kiểm duyệt và đạt chứng nhận hữu cơ Châu Âu. Mì ống penne lứt hữu cơ được nhập khẩu chứa nhiều chất dinh dưỡng tốt cho người tiêu dùng.', 'https://salt.tikicdn.com/cache/750x750/ts/product/c5/e6/a9/67e04409fe79b8adb02c3891836c059a.jpg.webp', 33000, 'Canada', 44, 259, 560, '1 gói', 86000, 5),
(166, 'Mì spaghetti trắng hữu cơ', 'Là loại mì thường được dùng công thức nấu ăn mì sốt cà chua và thịt bò bằm bolognese hoặc sốt kem carbonara. Bảo quản ở nơi thoáng mát khô ráo, tránh nguồn nhiệt nóng và ẩm ướt.', 'https://salt.tikicdn.com/cache/750x750/ts/product/46/84/39/cf0877cd6eeb06699523626199992d0d.jpg.webp', 98000, 'Việt Nam', 47, 126, 279, '1 gói', 172000, 5),
(167, 'Mứt Cam Preserves Golden Farm', 'Mứt Cam Preserves Golden Farm nổi bật với hương vị tự nhiên, hậu chua ngọt thanh mát, giúp bạn dễ dàng chế biến các món ngon, nhanh gọn cho bữa sáng, bữa xế hoặc món ăn vặt cho bé yêu và gia đình.', 'https://salt.tikicdn.com/cache/750x750/ts/product/cb/15/10/7112c20fb9fcc75ed021f21d8a6e42ff.jpg.webp', 103000, 'Việt Nam', 45, 196, 306, '1 lọ', 149000, 10),
(168, 'Mứt Dâu Preserves Golden Farm', 'Mứt Dâu Preserves Golden Farm nổi bật với hương vị tự nhiên, hậu chua ngọt thanh mát, giúp bạn dễ dàng chế biến các món ngon, nhanh gọn cho bữa sáng, bữa xế hoặc món ăn vặt cho bé yêu và gia đình.', 'https://salt.tikicdn.com/cache/750x750/ts/product/27/6f/c0/f5c87a5267dfcc8d1537f8ebd171fab6.jpg.webp', 48000, 'Pháp', 43, 156, 477, '1 lọ', 107000, 10),
(169, 'Vina Mứt Kiwi', 'Hương trái cây tự nhiện, thành phần xác trái cây đậm đặc thơm ngon, dễ dàng pha trộn với các dòng sản phẩm khác. Màu sắc bắt mắt, hương thơm nhẹ cùng hương vị ngon ngọt kết hợp chút vị chua, kích thích vị giác của bạn. Mứt trái cây thích hợp dùng để pha chế.', 'https://bizweb.dktcdn.net/100/421/036/files/mut-vina-kiwi-tobee-food-1.jpg?v=1642215539810', 117000, 'Canada', 46, 197, 356, '1 lọ', 188000, 10),
(170, 'Vina Mứt Vải', 'Vina Mứt Vải là Dòng sản phẩm chuyên dụng pha chế các thức uống. Hương trái cây tự nhiện, đậm vị dễ dàng pha trộn với các dòng sản phẩm khác. Hương thơm nhẹ của vải cùng vị ngon ngọt. Mứt thích hợp dùng để pha chế.', 'https://salt.tikicdn.com/cache/w1200/ts/product/02/50/38/4ecfb025ef4a74aa3041e8fd71352bbc.png', 35000, 'Hàn Quốc', 46, 143, 396, '1 lọ', 124000, 10),
(171, 'Vina Mứt Việt Quất', 'Vina Mứt Việt Quất là Dòng sản phẩm chuyên dụng pha chế các thức uống. Hương trái cây tự nhiện, đậm vị dễ dàng pha trộn với các dòng sản phẩm khác. Màu sắc bắt mắt, hương thơm nhẹ cùng hương vị ngon ngọt kết hợp chút vị chua, kích thích vị giác của bạn. Mứt thích hợp dùng để pha chế.', 'https://salt.tikicdn.com/cache/525x525/ts/product/c1/43/15/37f4042d45b516afc01be136cd921453.png', 53000, 'Việt Nam', 40, 170, 537, '1 lọ', 113000, 10),
